insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(1,'1000억 막걸리 프리바이오',5,3500,'쌀(국내산), 밀(국내산), 누룩, 정제수','(주)국순당','강원도 횡성군 둔내면 강변로 975','강원도 횡성군','TAK','옅은 복숭아의 색을 가진 막걸리로 잘 발효된 쌀의 향, 그리고 밀의 구수함, 부드러운 새콤함이 공존하는 향을 가지고 있다. 첫맛은 유산균의 활동으로 산뜻한 새콤함이 몰려오며, 이내 경쾌한 탄산감이 다가온다. 마지막은 막걸리 특유의 고소함으로 마무리를 한다. 요구르트와 유사한 식감을 가지고 있으며, 막걸리 초보자에게 추천하고 싶은 맛이기도 하다. 새콤함이 좋아서 함께 참외, 수박, 오이 등 담백한 과실 및 채소와도 잘 어울리는 맛이다. 여름에는 시원하게 생맥주 잔에 마셔도 전혀 어색하지 않은 청량감도 가지고 있다.',3,4,3,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(2,'1000억 유산균막걸리',5,3200,'쌀(국내산), 밀(국내산), 누룩, 정제수','(주)국순당','강원도 횡성군 둔내면 강변로 975','강원도 횡성군','TAK','잘 발효된 아이보리 컬러의 막걸리이다. 첫 번째로 다가오는 향은 신맛과 누룩향의 조화. 입안에 머금은 순간 화려한 산미가 입안에 확 퍼지면서 식욕을 돋우게 한다. 바로 담백한 쌀 맛이 느껴지며 마지막은 경쾌한 단맛의 잔상이 그려진다. 새콤함과 달콤함의 조화가 좋은 만큼 여름에 얼려서 슬러쉬로 먹어보면 무척 매력이 있을 듯하며, 신맛을 필요로 하는 보쌈, 족발 요리와의 조합이 기대된다. 운동 전후에도 마시면 피로감이 풀릴 듯 한 감식초와 같은 식감도 느껴진다.',3,5,5,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(3,'1932 새싹땅콩 햅쌀막걸리',6,15000,'쌀(국내산), 땅콩농축액, 입국, 효모, 고과당, 정제효소제, 정제수','(주)1932 포천일동막걸리','경기도 포천시 일동면 운악청계로 1597','경기도 포천시','TAK','술 빛이 매우 희고 곱다. 향이 강하지는 않지만 식물의 향이 올라오고 술을 입 안에 넣으면 단맛이 올라오고 그 다음에 천천히 올라오는 땅콩의 고소함이 입 전체에 스며든다. 목넘김은 매우 부드럽고 크리미하다. 여성들이 즐겨 먹을 수 있는 술이며 식후주로 적합하다. 케이크 등 디저트 음식과 잘 어울린다.',2,2,3,1,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(4,'1932 새싹땅콩 흑미막걸리',6,15000,'쌀(국내산), 흑미(15%), 땅콩농축액, 입국, 효모, 고과당, 정제효소제, 정제수','(주)1932 포천일동막걸리','경기도 포천시 일동면 운악청계로 1597','경기도 포천시','TAK','흑미로 술을 빚어 검붉은 색을 가지고 있으며, 곡물향보다는 상큼한 향이 좀 더 강하게 올라온다. 입에 넣으면 탄산에 의한 청량미가 도드라지고 그 뒤로 단맛이 쫒아오는 형국이다. 산미가 있어 전체적으로 가볍고 목넘김이 매우 깔끔하다. 돼지 갈비 등 구은 고기와 잘 어울린다.',3,1,1,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(5,'33JU',18,49000,'산양산삼, 주정, 결정과당, 효소처리스테비아, 에리스리톨, 호박산, 꿀','농업회사법인 33가 주식회사','강원도 홍천군 남면 어두원이길 143','강원도 홍천군','SPI','산양산삼 고유의 풍미가 진하게 느껴지고 부드러운 목 넘김으로 육사시미, 소고기구이, 회, 삼겹살과 잘 어울린다.',3,0,0,1,0,4,1);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(6,'52C',17.50,10000,'정제수, 주정, 쌀증류식소주원액, 오이','농업회사법인(주)청산녹수','전라남도 장성군 장성읍 남양촌길 (백계리) 19','전라남도 장성군','SPI','다소 알뜰하게 파먹은 듯한 수박향이 추억을 소환한다. 시원한 오이향과 쌈싸름한 술맛이 오묘히 어우러진다. 테이블의 52C에 겁먹지 말 것! 술술넘어가는 17.5도의 COOL한 술이다. 곱창구이와 잘 어우러진다.',3,0,0,3,0,4,2);
