insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(23,'감악산 머루주',12,28000,'국내산 산머루원액 78.5%, 주정','산머루농원영농조합법인','경기도 파주시 윗배우니길 441-25','경기도 파주시','WIN','잘 익은 머루 특유의 검붉은 색을 가지고 있다. 묵직한 병에서 느껴지는 권위적인 분위기와는 달리, 뚜껑을 열면 달콤한 향이 이 술이 가진 스위트함을 느끼게 해 준다. 첫 느낌은 단맛으로 시작되지만 마지막은 식욕을 불러오는 산미로 잘 마무리된다. 파주의 술인 만큼 임진강에서 잡히는 메기, 참게 요리와 잘 어울릴 듯하며, 흑염소, 오리로스구이 등 보양식과도 좋은 궁합이다. 와인을 잘 못 마신다면, 추천하고 싶은 과실주이기도 하다.',4,4,3,3,0,0,0);
