insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(25,'감자술 13도',13,14000,'백미(국내산), 감자(국내산), 누룩, 효모, 정제수','오대서주양조','강원도 평창군 진부면 영정게길57-1','강원도 평창군','CHE','살짝 갈아놓은 감자에서 느낄 수 있는 식감이 있다. 맑은 연두빛의 개성 있는 색깔에 담백한 단맛이 느껴진다. 너무 맵고 짠 음식은 자칫 감자술의 은은한 맛이 가려질 수 있기 때문에 자극적이지 않은 안주를 추천합니다. 기름진 감자전하고 즐기면 딱 좋은 맛이다.',3,2,3,2,0,0,0);
