insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(33,'계룡백일주 12년',40,150000,'찹쌀, 백미, 누룩, 재리종국화꽃, 오미자, 진달래, 재래종 솔잎','계룡백일주','충청남도 공주시 봉정길 32','충청남도 공주시','SPI','계룡백일주 40%짜리보다 좀 더 다양한 향기가 올라온다. 외국의 잘 만든 진(GIN)을 먹는 것 같고 숙성이 잘 되어 도수가 높지만 술이 매우 부드럽고 목넘김 또한 좋다. 입 안에 솔잎 등에서 나는 은은한 잔맛이 기분을 좋게 한다. 특별한 날 함께 나누기에 좋은 술이다. 잔칫상 음식과 두루 어울린다.',3,0,0,3,0,3,0);
