insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(56,'그랑꼬또 레드와인',12,35000,'포도(캠벨얼리)','그린영농조합법인','경기도 안산시 단원구 뻐꾹산길 107(대부북동)','경기도 안산시','WIN','맑은 포돗빛을 품고 있는 캠벨얼리 와인이다. 입안에 머금으면 상큼한 포도향부터 시작하며, 그 위로 단맛과 신맛, 그리고 미세한 타닌감도 뒤따라온다. 육류보다는 채소, 또는 해산물을 추천하고 싶은 와인이며, 육류와 즐긴다면 고추장 및 된장보다는 간장으로 양념이 되어 있는 요리가 와인의 맛을 잘 살려줄 듯하다. 레드 와인이지만 라이트함이 있는 만큼 시원하게 마신다면 생선요리, 나물 요리와도 잘 어울리는 로제 와인스러운 모습도 가지고 있다.',3,3,3,2,0,0,0);
