insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(58,'그랑꼬또 청수',12,73500,'포도(청수)','그린영농조합법인','경기도 안산시 단원구 뻐꾹산길 107(대부북동)','경기도 안산시','WIN','산미와 은은한 단맛의 조화가 좋고, 청사과, 레몬, 황도, 망고, 파인애플, 리치 등 여러 과실의 맛이 조금씩 녹아있다. 파삭한 질감과 미네랄도 느껴지며 향과 맛, 균형감이 모두 좋은 여운이 느껴지는 매력적인 와인이다. 어떤 음식이나 분위기와도 잘 어울릴 듯 하다. 굴찜, 조개찜, 대게찜 등 부드럽게 익힌 어패류나 해산물과 잘 맞을 듯 하다.',2,4,4,3,4,0,0);
