insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(73,'김포약주',11,10000,'옥수수 전분, 밀, 국, 효모, 젖산, 효소제, 정제수','김포양조','경기도 김포시 월곶면 김포대로 2763','경기도 김포시','CHE','맑고 투명한 황금빛의 색을 가지고 있으며, 단맛과 신맛 등의 조화가 잘 갖춰 있어 어느 맛 하나 튀지 않는다. 약주류 중에서는 알코올 도수가 낮아(11%) 입 안의 풍미와 목 넘김이 가볍게 느껴진다. 마시고 난 후에도 여운이 길지 않아 누구나 편하게 즐길 수 있는 술이다. 닭백숙 등 양념되지 않은 고기류와 잘 어울린다.',0,1,2,1,2,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(74,'김포예주 프리미엄',13,18000,'국내산 쌀, 국, 효모, 젖산, 정제수','김포양조','경기도 김포시 월곶면 김포대로 2763','경기도 김포시','CHE','연한 호박색의 약주로 은은한 고소함이 올라오며, 단맛과 쓴맛이 처음에 느껴지고 시간이 지나면서 알코올에 의한 자극이 입 안을 꽉 차게 만든다. 술을 마시고 난 후에 아주 긴 여운이 남는 것이 매력인 술이다. 불고기와 잘 어울린다.',2,2,1,1,4,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(75,'김포특주',15,3000,'국내산 쌀, 효모, 정제효소, 정제수, 프락토올리고당','김포금쌀탁주영농조합법인','경기도 김포시 하성면 하성로 622','경기도 김포시','CHE','첫인상은 진한 발효된 맛과 향, 뒷맛은 나풀거리는 가벼움. 결국 진함과 가벼움 모두가 공존하는 약주다. 향이 강한 편이라 진한 한국음식과 잘 어울린다. 감자탕, 김치찌개, 청국장, 매운 제육볶음 등이 대표적이다.',0,4,4,2,2,0,0);
