insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(83,'녹파주',15,35000,'국내산 찹쌀, 멥쌀, 누룩, 정제수','농업회사법인(주)솔송주','경상남도 함양군 지곡면 지곡창촌길3','경상남도 함양군','CHE','잔잔하고 은은한 향에서 출발하여, 매끄러운 단맛으로 끝나는 술. 튀지 않는 맛에서 기품이 느껴지는 술이다. 향에서 올라오는 은은한 맛을 즐기기 위하여 강하지 않은 음식을 추천하고 싶다. 특히 녹파주가 나오는 경남 함양에서는 드넓은 연꽃 밭에서 나오는 연잎밥이 유명한데, 같이 즐기기에 충분히 매력적인 궁합이다.',3,2,2,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(84,'뉴트로',8,6000,'찹쌀(국내산), 멥살(국내산), 누룩, 레몬, 라임, 잉글리쉬블랙퍼스트, 정제수','디오케이브루어리','서울특별시 강북구 삼양로 455, 1층','서울특별시 강북구','TAK','디자인과는 다르게 구수한 미숫가루와 같은 색을 가지고 있다. 코에 잔을 대자 레몬의 향이 먼저 치고 올라오며 바로 라임의 향이 뒤따라 온다. 새콤한 맛에서 시작을 해서 달콤함을 거쳐 산뜻한 맛으로 마무리가 되는 전혀 막걸리스럽지 않은 독특한 맛이다. 마시고 난 후에는 살짝 목에서 따뜻함이 느껴질 정도로 일반 탁주에 비해서는 알코올 도수가 높다. 여름에 시원한 샐러드와 함께 즐긴다면 더할 나위 없고, 상큼함이 좋아 피자, 치킨 등 맥주 대용으로도 매력적이다.',4,4,4,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(85,'느낌 19.8도',19.8,4000,'백미(국내산), 누룩, 효모, 정제수','농업회사법인 명품안동소주 (주)','경상북도 안동시 풍산읍 유통단지길 124-34','경상북도 안동시','SPI','증류주는 기본적으로 도수가 낮으면 밍밍하고 물 맛이 많이 나는 것이 단점인데, 안동소주 느낌 19.8%는 일반 증류식소주에 비해 알코올도수가 낮은데도 불구하고 전통소주에서 느껴지는 곡향이 입 안을 가득 채워주는 것이 가장 큰 특징이다. 아주 차게 마시기보다는 실온에 좀 두고 곡향이 올라올 때 시음하는 것을 권장한다. 양념이 된 고기류와 잘 어울린다.',0,0,0,2,0,2,2);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(86,'느낌 30도',30,6500,'백미(국내산), 누룩, 효모, 정제수','농업회사법인 명품안동소주 (주)','경상북도 안동시 풍산읍 유통단지길 124-34','경상북도 안동시','SPI','안동소주 느낌은 전통 증류식소주에서 느낄 수 있는 불맛과 곡물의 향을 잘 살린 증류주다. 강하지는 않지만 옅은 단맛과 뒤에 올라오는 약간의 쓴맛이 매력적이다. 안동소주 느낌 30%도 아주 차게 마시기 보다는 실온에 두고 증류향을 느끼면서 마시길 바란다. 도수가 너무 높다고 느끼면 온더락으로 즐기는 것 또한 방법이다. 석쇠 불고기와 잘 어울린다.',0,0,0,3,0,3,3);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(87,'느린마을 막걸리',6,3600,'쌀, 국, 효모, 정제수','배상면주가 포천LB','경기도 포천시 화현면 화동로 432번길 25','경기도 포천시','TAK','생쌀발효법의 특징을 여실히 보여주는 막걸리이다. 메론, 사과, 배 등을 연상하게 하는 풍부한 과실향이 장점이다. 곱고 부드러운 질감과 과하지 않은 산미, 탄산이 조화롭게 어우러져 품위 있는 맛을 낸다. 치즈, 봉골레 파스타, 꼬꼬뱅, 회 무침 등이 어울린다.',3,2,2,4,4,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(88,'능이주',13,12000,'국내산 쌀, 능이농충액, 누룩, 정제수','내국양조','충청남도 논산시 연무읍 마종로 361','충청남도 논산시','CHE','옅은 호박 색깔의 술로 신선한 무화가 향이 올라오는 것이 특징이다. 술 맛은 산뜻한 산미가 중심을 이루고 있어 술이 묵직하지 않고 가볍게 느껴진다. 목넘김은 깔끔하고 먹고 난 후에도 산뜻함이 남아있어 양념된 음식보다는 버섯을 살짝 구어 함께 마시는 것이 좋다.',1,3,1,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(89,'니모메',11,33000,'백미, 누룩, 감귤 진피, 효모','제주샘영농조합법인','제주특별자치도 제주시 애월읍 애원로 283','제주특별자치도 제주시','CHE','제주도만이 가질 수 있는 시트러스 계역의 약주다. 은은히 올라오는 감귤향에 뭉근히 올라오는 곡물 특유의 매력이 있다. 전체적으로 단맛이 도는 만큼 전통주 입문자에게도 충분히 도전할 만한 제품이다. 전체적으로 균형미가 좋으며 귤 향과 새콤함이 술의 특징을 만들어준다. 칠리새우, 닭강정, 겨울의 방어회, 흑돼지 삼겹살 등 기름진 음식과 잘 어울린다.',3,3,1,3,4,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(90,'다래와인 3004',8,10000,'참다래','영농조합법인 오름주가','경상남도 사천시 미룡길 31-20','경상남도 사천시','WIN','진한 황색 빛깔의 술로 푹 익은 다래에서 나는 진한 과실향이 올라온다. 입에 넣으면 점도 있으면서 묵직하게 입 안 전체를 채우고 단맛과 신맛의 조화가 잘 갖춰져 있다. 목넘김이 매우 부드럽고 먹고 난 후에 잡맛이 없이 깔끔하다. 소고기 구이와 잘 어울린다.',2,2,1,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(91,'다래와인 7004D',12,28000,'참다래','영농조합법인 오름주가','경상남도 사천시 미룡길 31-20','경상남도 사천시','WIN','맑은 담황색을 가지고 있으며 처음 발효향이 강하게 올라오기 때문에 시음하기 전에 산소와의 접촉을 많이 하고 시음하는 것이 좋은 술이다. 단맛이 거의 없고 산미가 술 맛 전체를 이끌어간다. 목넘김은 깔끔하고 술이 전체적으로 가볍다. 스파게티와 같은 면 요리와 잘 어울린다.',1,4,2,2,0,0,0);
