insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(106,'도구 막걸리',6,15000,'밀(외국산), 쌀(국내산, 외국산), 물엿, 효모, 조제종국, 정제효소제, 아스파탐, 젖산','동해명주','경상북도 포항시 남구 동해면 일월리 51-1','경상북도 포항시','TAK','술의 느낌이 매우 가볍다. 쌀과 함께 들어간 밀가루가 쌀의 가벼움을 보완해주고 술을 마시고 난 후에 입 안을 꽉 채워주기 때문에 첫 맛은 가벼우나 뒷 맛은 묵직한 것이 특징이다. 적절한 단맛과 산미를 가지고 있어 양념이 많이 된 생선 조림과 잘 어울리다.',2,2,3,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(107,'도깨비술 11도',11,12000,'멥쌀(국내산), 누룩, 효모, 정제수','농업회사법인 (주)도깨비양조장','충청북도 단양군 가곡면 사평3길 5','충청북도 단양군','TAK','부드러운 아이보리 컬러가 인상적인 막걸리다. 잘 익은 누룩향에 사과향, 배향이 뭉근히 올라온다. 첫 느낌은 단맛과 신맛이 좋은 조화를 이루고 있으며, 마무리는 미세한 새콤함으로 마무리된다. 식중주 막걸리로 손색이 없으며, 좀 더 단맛을 즐기고 싶다면 1주일 정도 냉장고에서 숙성해서 즐기면 단맛이 적어지는 만큼 또 다른 맛을 만날 수 있다. 단양에서 만들어지는 만큼 마늘 정식 등과 즐기면 좋으며, 쏘가리 매운탕, 메기 매운탕 등의 민물고기 요리와 곤드레 밥 등의 나물 요리도 좋은 매칭을 보인다.',4,4,3,5,0,0,0);
