insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(110,'도로시 머루주',12,25000,'국내산 산머루원액 78.5%, 주정','산머루농원영농조합법인','경기도 파주시 윗배우니길 441-25','경기도 파주시','WIN','머루 특유의 검붉은 색을 가지고 있다. 향에서 느껴지는 달콤한 향이 이 술이 가진 스위트함을 느끼게 해 준다. 첫 느낌은 단맛으로 시작되지만 마지막은 식욕을 불러오는 산미로 잘 마무리된다. 파주의 술인 만큼 임진강에서 잡히는 메기, 참게 요리에 삼계탕, 오리백숙 등의 보양식과도 잘 어울린다.',4,4,3,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(111,'도문대작 생막걸리',10,3000,'강릉 쌀, 누룩, 갯방풍나물, 정제수','방풍도가','강원도 강릉시 미노길 103','강원도 강릉시','TAK','튀지 않고 은은하게 올라오는 곡물 발효향이 좋고, 단맛이 적고 적절한 산미가 입 안의 느낌을 매우 깔끔하게 만들어 준다. 타 막걸리에 비해 도수가 높지만(10%) 자극적이지 않고 목 넘김이 부드럽다. 갈비찜과 잘 어울린다.',1,3,3,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(112,'독산 53도',53,25000,'쌀(국내산), 국, 효모, 정제수','농업회사법인 오산양조(주)','경기도 오산시 시장길 63','경기도 오산시','SPI','곡물로 빚은 술을 증류한 전통 증류주로 진한 소주의 맛을 원한다면 독산53을 마셔봐야 한다. 향은 강하지 않지만 입 안에서의 느낌이 매우 강하다. 술을 마시고 난 후에 아주 긴 여운이 남는 술이다. 여성보다는 소주를 좋아하는 남성분들에게 추천하고 싶은 술이다. 차게해서 마시는 것이 좋다. 양념 되지 갈비와 잘 어울린다.',0,0,0,4,0,2,5);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(113,'동강더덕주',20,3500,'더덕, 대추구기자, 감초, 원지','영월더덕영농조합법인','강원도 영월군 주천면 솔치로 633','강원도 영월군','SPI','연한 담갈색의 빛깔로 더덕의 향이 은은하게 입안에 감돈다. 집에서 담든 더덕주와는 달리 더덕향과 알코올 도수가 과하지 않게 적절하게 매치되어 가볍게 마실 수 있다. 더덕양념구이와 함께 하면 더욱 좋다.',3,0,0,2,0,3,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(114,'동몽',17,30000,'홍천 쌀, 정제수, 단호박, 국(밀 누룩)','예술','강원도 홍천군 내촌면 물걸리 508-2','강원도 홍천군','CHE','맑은 황금빛의 술로 익은 호박향이 나며 독특한 단맛을 가지고 있다. 전체적으로 맛의 균형이 잘 갖춰져 있으며 단호박이 들어가 독특한 풍미를 가지고 있다. 두부전골과 잘 어울린다.',2,3,1,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(115,'동정춘',8,18000,'쌀, 누국, 정제수','농업회사법인(주)산수','강원도 홍천군 화촌면 야시대로 211-57','강원도 홍천군','TAK','진한 아이보리색을 가지고 있으며 달콤하면서도 바닐라 향이 은은하게 올라온다. 쌀양 대비 물이 적게 들어가 단맛이 강하고 약한 산미가 지나친 단맛을 잡아주는 모습이다. 입 안에서는 단맛으로 꽉 차는 느낌이 들고 마시고 난 후에는 오히려 여운이 짧고 고소하다. 갈비찜 등 단맛이 약간 있는 음식들과 잘 어울린다.',4,3,2,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(116,'동학 1957',13,3500,'쌀(국내산), 구연산, 효모, 젖산, 정제효소제, 국, 주정, 정제수','고헌정영농조합법인','충청북도 충주시 산척면 샛강영길 39','충청북도 충주시','CHE','깨끗한 담황색인 청주다. 코끝에서 뭉근하게 느껴지는 쌀 향이 특징으로 느껴진다. 입안에 머금은 순간 담백함이 먼저 시작되며, 이내 쌀 특유의 부드러운 단맛과 가벼운 산미로 마무리된다. 어디 하나 모난 곳이 없어 밸런스가 좋은 술이라고 할 수 있다. 식중주로 좋은 역할을 하는 술이며, 다채로운 한식과 잘 어울리는 스타일이다. 세심한 매칭보다는 부담 없이 어울리는 스타일로 한정식 등과 함께 즐기면 이 술을 충분히 즐길 수 있다.',3,3,2,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(117,'동해 동동주',6,15000,'쌀(국내산, 외국산), 물엿, 효모, 조제종국, 정제효소제, 아스파탐, 젖산','동해명주','경상북도 포항시 남구 동해면 일월리 51-1','경상북도 포항시','TAK','술을 잔에 따르면 동해동동주라 불리우는 것처럼 쌀이 위에 동동 떠 있는 모습을 볼 수 있어 재미있게 즐길 수 있는 술이다. 또한, 술을 입에 넣으면 먼저 곡물의 고소한 향미가 입 전체를 채우고 단맛이 적어 매우 가벼운 느낌이다. 마시고 난 후에는 약간의 쓴맛이 남는데 이것이 음식을 먹게 만든다. 문어 숙회와 잘 어울린다.',2,1,3,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(118,'두레앙 22도',22,5000,'거봉포도원액','농업회사법인 (주)두레양조','충청남도 천안시 서북구 입장면 율목길17-6','충청남도 천안시','SPI','포도를 발효시켜 증류한 술로 도수가 낮아 포도의 향은 많이 올라오지 않지만 입 안에서의 느낌은 달고 포도의 느낌을 더 깊게 받을 수 있다. 향과 맛이 전혀 다른 술이다. 마실 때 얼음을 넣어 함께 마시면 좋다. 매운 김치찜이나 양념이 강한 음식과 잘 어울린다.',0,0,0,4,0,1,2);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(119,'두레앙 브랜디',35,70000,'거봉포도원액','농업회사법인(주)두레양조','충청남도 천안시 서북구 입장면 율목길17-6','충청남도 천안시','WIN','진한 포도색에 위스키와 같은 오크향이 가득하게 올라온다. 부드러운 단맛이 처음에 올라오고 그 뒤로 은은한 쓴맛이 자리를 잡는다. 알코올이 입술과 혀를 자극시키고 술을 마시고 난 후에도 입 안에 풍미가 가득하다. 굴 요리와 잘 어울린다.',3,0,0,3,0,4,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(120,'두레앙 와인',12,15000,'거봉포도원액','농업회사법인(주)두레양조','충청남도 천안시 서북구 입장면 율목길17-6','충청남도 천안시','WIN','로제와인 색을 가지고 있으며 바로 마시는 것 보다는 디캔팅을 통해 좋지 않은 향을 날려버리고 마시는 것이 좋다. 단맛과 신맛의 조화가 잘 갖춰져 있으며 목넘김 또한 무겁지 않고 부드럽게 넘어간다. 어떤 음식과도 잘 어울릴 수 있는 술이나 면요리와 특히 잘 어울린다.',2,2,1,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(121,'두레앙 증류주',35,35000,'거봉포도원액','농업회사법인(주)두레양조','충청남도 천안시 서북구 입장면 율목길17-6','충청남도 천안시','SPI','맑은 미색을 가지고 있으며 포도껍질향이 산뜻하게 올라온다. 첫 맛은 순하지만 바로 포도와 알코올에서 진한 맛이 입 전체를 채운다. 목넘김은 매우 부드럽고 술을 마시고 난 후에는 알코올이 혀를 자극하지만 혀에 시원함이 남는다. 과일 샐러드 등과 잘 어울린다.',2,0,0,2,0,2,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(122,'두리향',43,36000,'국내산 찹쌀, 누룩, 정제수','케이소주 (전주가양주연구소)','전라북도 전주시 완산구 중산중앙로27. 3층','전라북도 전주시','SPI','전형적인 증류식 소주의 투명색. 구수한 누룽지 향이 느껴지는 좋은 곡물의 향이다. 알코올 도수가 높아 거부감이 있을 수 있지만, 끝에서 느껴지는 기분 좋은 단맛이 있으며, 목 넘김 역시 매끈하며 부드러운 좋은 맛이다. 알코올 도수가 43%나 되는 만큼, 얼음이나 소다와 같이 즐겨도 좋을 듯하며, 육전 및 갈비찜 등 진한 요리와도 잘 어울리는 맛이다.',0,0,0,4,0,3,3);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(123,'두유노',6,6000,'찹쌀(국내산), 멥살(국내산), 누룩, 밀가루','디오케이브루어리','서울특별시 강북구 삼양로 455, 1층','서울특별시 강북구','TAK','맑은 우윳빛이 첫인상으로 다가오는 술이다. 코끝을 대보면 잘 익은 숙성 치즈향부터 느껴진다. 감식초에서 느껴지는 부드러운 새콤함에서 시작하여 담백함을 거쳐 가벼운 단맛으로 터치감을 마무리한다. 기존의 막걸리와 비슷한 듯 다른 식감을 가지고 있으며, 600ml의 비교적 작은 사이즈로 그립감이 좋은 막걸리라고 볼 수 있다. 무감미료 막걸리인 만큼 단맛은 비교적 적지만 거추장스러운 맛이 없어 오히려 깔끔하게 느껴진다. 피자 및 치킨, 그리고 떡볶이와 같이 기름지거나 강한 맛을 가진 음식에 추천한다.',2,3,3,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(124,'라이스퐁당 13도',13,15000,'찹쌀(국내산), 누룩, 정제수(음양곽, 감초)','농업회사법인 (주)벗드림','부산광역시 북구 덕천로247번길 3, 2층','부산광역시 북구','CHE','옅은 호박색을 가진 술로 삼지구엽초와 감초가 들어가 약재를 푹 삶았을때의 풀 향이 매우 특징적인 술이다. 멥쌀이 아닌 찹쌀만 이용해 술을 빚어 맛있는 단맛이 매력적이고 뒤에 잘 익은 과일의 산뜻한 산미가 입 안을 개운하게 해준다. 힘든 하루를 보내고 나서 약재를 넣고 푹 달인 삼계탕과 함께하면 보약이 따로 없을 듯 하다.',3,2,1,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(125,'라이스퐁당 17도',17,18000,'찹쌀(국내산), 누룩, 정제수(음양곽, 감초)','농업회사법인 (주)벗드림','부산광역시 북구 덕천로247번길 3, 2층','부산광역시 북구','CHE','라이스퐁당 13%에 비해 전체적으로 맛과 향이 짙다. 옅은 호박색을 가진 술로 삼지구엽초와 감초가 들어가 약재를 푹 삶았을때의 풀 향이 매우 특징적인 술이다. 멥쌀이 아닌 찹쌀만 이용해 술을 빚어 맛있는 단맛이 매력적이고 뒤에 잘 익은 과일의 산뜻한 산미가 입 안을 개운하게 해준다. 진한 양념으로 만든 닭볶음탕이 제격이다.',3,3,1,4,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(126,'레돔 내추럴 스파클링 로제와인',7.5,38000,'포도(99.99%)','농업회사법인 작은알자스','충청북도 충주시 엄정면 도자기길 32','충청북도 충주시','WIN','상큼한 포도 향이 그대로 느껴지는 충주의 스파클링 와인. 코르크를 열면 펑 하는 소리가 샴페인을 그대로 떠오르게 한다. 가볍고 경쾌한 느낌이 신선하다는 이미지를 그대로 느끼게 해 준다. 여운은 길지 않지만, 잔잔한 과실향이 입안을 맴돈다. 식전주로도 좋지만, 단맛이 과하지 않아 식중주로도 충분히 쓰일 수 있다.',3,3,4,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(127,'레돔 시드르',6,32000,'사과(100%)','농업회사법인 작은알자스','충청북도 충주시 엄정면 도자기길 32','충청북도 충주시','WIN','잔잔한 사과 향이 묻어나오는 스파클링 사과와인. 역시 코르크를 열면 펑 하는 소리가 축배를 들기에 딱 좋은 모습이다. 천연 탄산이 주는 부드러운 탄산에 사과 껍질에서 느껴지는 누룩향 느껴지는 것이 우리 사과 와인에서는 느끼지 못하는 독특함이 있다.',3,3,4,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(128,'려(驪)고구마증류소주 25도',25,0,'고구마(여주산 100%)','(농)국순당 여주명주(주)','경기도 여주시 강천면 부평로 330','경기도 여주시','SPI','향긋한 과실향이 물씬 올라와 코를 자극시킨다. 알코올 도수에 맞는 술 맛을 가지고 있으며 입에 넣는 순간 입 전체로 알코올이 퍼지는 느낌이 좋고 술을 마시고 난 후에 은은한 쓴맛이 음식을 부르게 만든다. 생굴과 잘 어울린다.',1,0,0,3,0,2,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(129,'려(驪)고구마증류소주 40도',40,0,'고구마(여주산 100%)','(농)국순당 여주명주(주)','경기도 여주시 강천면 부평로 330','경기도 여주시','SPI','은은한 과실향이 올라오고 숙성이 잘 되어 입 안에 넣으면 아주 부드러운 퍼짐을 가지고 있다. 술을 마시고 난 후에 아주 신선한 산미가 남는데 매우 고급스런 맛이다. 알코올 퍼지는 느낌과 여운이 멋진 술이다. 활어회와 아주 잘 어울린다.',0,0,0,3,0,2,2);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(130,'려(驪)증류소주 25도',25,30000,'고구마(여주산 100%), 쌀(여주산 100%)','(농)국순당 여주명주(주)','경기도 여주시 강천면 부평로 330','경기도 여주시','SPI','포도를 증류한 브랜디에서 나는 향긋한 향기가 물씬 올라와 기분을 좋게 만들어 준다. 술을 입에 넣으면 마치 맑은 청주를 마시는 느낌이다. 도수가 높지만 매우 부드럽고 술을 마시고 난 후에 자극적이지 않아 누구나 편하게 마실 수 있는 술이다. 양념이 과하지 않은 음식과 잘 어울린다.',0,0,0,1,0,3,1);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(131,'로얄 안동소주 18년',45,72000,'국내산 쌀, 입국','농업회사법인 유토피아(주)','경상북도 안동시 남후면 광음리 1111-2','경상북도 안동시','SPI','알코올도수는 높지만 향은 강하지 않다. 입과 혀를 자극시키지 않아 숙성이 잘 된 술이며 입 안에서 잘 퍼져 들어간다. 술을 마시고 난 후에 입 안의 화끈거림이 증류식소주를 마셨구나 다시 생각나게 만들어 준다. 굴 요리가 생각난다.',3,0,0,4,0,1,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(132,'르깔롱',16,35000,'정제수, 주정, 고과당, 죽력, 메론후레바, 레이지라임, 코코넛향, 윌튼스카이아이싱칼라','추성고을','전라남도 담양군 용면 추령로 29','전라남도 담양군','SPI','맑은 에메랄드 빛깔을 가진 술로 멜론 향기가 진하게 올라온다. 도수가 높지만 단맛이 강해 목넘김이 매우 깔끔하고 입 안에 느껴지는 달콤한 코코넛의 느낌이 술을 계속 마시게 만들어 준다. 아주 차게 마시는 것 보다는 온도가 올라가면서 느껴지는 다양한 향을 즐기면서 마시기 좋은 술이다. 특별한 안주보다는 열대과일과 아주 잘 어울린다.',0,0,0,4,0,4,4);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(133,'마셔블랑',12,20000,'감귤과즙(한라봉), 백설탕, 액상효모, 무수아황산','농업회사법인(주)시트러스','제주특별자치도 서귀포시 남원읍 신례천로46','제주특별자치도 서귀포시','SPI','황금빛의 은은한 색깔이 매력적인 와인. 감귤의 향긋한 향이 그대로 살아있는 와인. 목 넘김에서 느껴지는 상쾌한 향이 입안을 기분 좋게 만든다. 후미는 길지 않은 편이나, 입안에서 맴도는 감귤의 여운으로 은근히 제주도의 푸른 바다가 떠오른다. 맛과 향을 방해하지 않는 가벼운 샐러드와 즐기면 이 술의 매력을 더욱 잘 느낄 것이다.',3,3,3,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(134,'마주 소곡주',16,17000,'찹쌀(국내산), 백미(국내산), 모싯잎, 우리밀 누룩, 정제수, 효모','옥순가 소곡주 마주','충청남도 서천군 서초면 시초동로 88-1','충청남도 서천군','CHE','다소 짙은 담황색을 가진 약주다. 향에서는 가벼운 새콤함이 올라오지만, 맛을 보는 순간 찹쌀이 주는 매끈함과 단맛이 그대로 올라오며 가벼운 신맛으로 마무리하며 식욕을 돋운다. 전체적으로 귀하게 빚었다는 느낌이 들며, 찹쌀의 단맛이 다소 강한 만큼 식전주나 식후주로 추천한다. 맛이 강한 음식과 함께 먹으면 이 술의 맛을 잘 못 느낄 수 있기 때문에 가급적 담백한 산채 요리 등이 잘 어울린다.',4,3,2,4,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(135,'마주 소화곡주',41,25000,'찹쌀(국내산), 백미(국내산), 모싯잎, 우리밀 누룩, 정제수, 홍고추','옥순가 소곡주 마주','충청남도 서천군 서초면 시초동로 88-1','충청남도 서천군','SPI','증류주답게 무색투명한 색을 그대로 가지고 있다. 조용할 듯 한 이 술의 매력은 향부터 시작하는데, 잘 익은 누룽지향을 그대로 내뿜는다는 것. 그리고 이 향이 마시고 난 후에도 입속에 계속 남는다. 첫맛은 식혜와 같은 담백한 단맛으로 시작하며, 살짝 새콤함도 스쳐가지만, 이내 짙은 증류식 소주의 묵직한 향이 아래부터 올라온다. 증류식 소주 초심자에게는 다소 어려울 수 있으나 적응되면 더욱 매력적으로 다가오는 맛이다. 서천에서 만들어지는 술인 만큼 서천의 명물인 아귀찜 등이 잘 어울리며, 얼음이나 차가운 탄산수를 넣어 즐기는 것도 추천해 본다.',0,0,0,4,0,3,3);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(136,'막시모40',40,49000,'오미자(국내산)증류원액, 오미자(국내산)원액, 정제수','농업회사법인(주)착한농부','경상북도 예천군 용문면 복천길 16-8','경상북도 예천군','SPI','옅은 담황색의 증류주로 달콤한 사탕과 박하 향기가 코를 자극시킨다. 도수가 높지만 적절한 단맛이 증류주를 부드럽게 만들어주고 입 안에 꽉 차는 시원한 느낌이 매우 좋다. 술을 마시고 난 후에 느껴지는 맛있는 쓴맛은 음식과의 조화를 잘 만들어 준다. 양념 불고기와 잘 어울린다.',0,0,0,3,0,4,3);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(137,'만강에 비친 달',10,12000,'홍천 쌀, 정제수, 단호박, 곡자(밀 누룩)','예술','강원도 홍천군 내촌면 물걸리 508-2','강원도 홍천군','TAK','연한 아이보리 색과 은은하게 올라오는 호박의 단 향이 좋으며 단맛과 신맛의 균형이 잘 갖춰져 있다. 진한 부드러움이 목을 스치고 지나간다. 찌개류와 잘 어울린다.',2,3,3,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(138,'맑은 문희주',13,36000,'유기농찹쌀 (국산), 멥쌀 (국산), 전통누룩 (우리 밀), 정제수','문경주조','경상북도 문경시 동로면 노은2리 49-15','경상북도 문경시','CHE','담황색의 술 빛에 수정과 향이 난다. 밀가루에서 나온듯한 맑은 산미와 벌꿀의 단맛이 술을 묵직하게 만들어 입 안을 자극시킨다. 여운이 오래간다. 봄에 나는 나물 무침과 잘 어울린다.',3,1,1,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(139,'맑은내일 WINery 단감명작',7,20000,'단감(국내산_창녕), 정제수, 설탕, 효소제, 무수구연산, 효모, 무수아황산(산아방지제), 소브산(보존료)','농업회사법인 우포의아침 주식회사','경상남도 창녕군 대지면 대지농공단지길 40','경상남도 창녕군','WIN','4~5 °C의 차가운 온도로 칠링하는 것이 좋다. 약간의 산미와 당도가 입맛을 기분 좋게 돋우므로 식전주로 마시기 제격이다. 샐러드류와 해산물과도 잘 어울리며, 케이크와 마카롱과 같은 디저트와 함께 하는 것도 좋다.',3,2,3,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(140,'맑은내일 발효막걸리',7,2200,'쌀(국내산), 누룩, 효소, 아스파탐, 정제수','농업회사법인 우포의아침(주)','경상남도 창녕군 대지면 대지농공단지길 40','경상남도 창녕군','TAK','다소 농도가 짙어 보이는 아이보리 컬러를 가지고 있다. 쌀과 누룩향과 적절히 조화된 향을 가지고 있으며 산뜻한 산미가 식욕을 자극한다. 새콤함으로 마무리될 듯한 맛은 후미에서 반전을 가져온다. 매끄러운 단맛이 함께 있기 때문. 따라서 이 막걸리는 막걸리 초심자가 즐기기에도 충분하다. 좋은 산미를 가져서 식욕을 돋우는 만큼, 한정식과 같은 다양한 요리가 있는 자리에도 잘 어울리며, 독특한 디자인으로 서양식 레스토랑에서도 결코 어색하지 않은 디자인이다. 함박 스테이크, 파스타와도 충분히 함께 즐길 수 있다.',3,3,3,4,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(141,'맑은내일 우포',12,5000,'쌀(국내산), 옥수수전분, 효모, 누룩, 양파, 정제수','농업회사법인 우포의아침(주)','경상남도 창녕군 대지면 대지농공단지길 40','경상남도 창녕군','CHE','약주 특유의 담황색을 가지고 있지만 한약 향 등은 전혀 느껴지지 않는 맑은술이다. 옥수수에서 나오는 듯 한 고소함이 있으며, 입속에 머금는 순간 적절한 부드러움이 느껴진다. 단맛, 신맛 등의 조화가 잘 이뤄져 있어서 부담되지 않는 맛과 향을 가지고 있다. 집안에서 홈술로 즐기기 좋은 술이며, 김치찌개, 된장찌개 등 일상의 음식과 즐기기 좋다.',3,2,2,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(142,'맑은술 해밀',15,25000,'쌀, 누룩, 정제수','내변산양조장','전라북도 부안군 행안면 부안농공단지길 14-11','전라북도 부안군','CHE','가벼운 멜론향으로 시작하는 색 다른 느낌이 있는 술이다. 미끈한 목 넘김에 진하지 않은 신맛과 잔잔한 여운으로 마무리되는 달콤한 맛이 특징이다. 약주 주종이 보통 단맛이 도는데 함께하는 음식은 생선요리 등과 같이 담백한 맛에 잘 어울린다.',3,2,2,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(143,'매실 막걸리',7,3500,'쌀, 매실과즙','농업회사법인(주)백운주가','전라남도 광양시 옥룡면 신재로 946-18','전라남도 광양시','TAK','아이보리 색의 막걸리로 과실 껍질에서 나는 향과 곡물의 고소한 향이 함께 올라온다. 청량한 맑은 단맛을 가지고 있으며 매실맛은 술을 마시고난 후에 살짝 올라온다. 매실을 이용했지만 산미는 적다. 더덕구이 등이 잘 어울린다.',3,2,1,1,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(144,'매실원주 15도',15,6000,'매실주원액, 발효주정, 정제수, 꿀','더한주류','서울시 은평구 증산로7길 28-13','서울시 은평구','SPI','맑은 담황색의 술로 상큼한 매실향이 강하다. 입에 넣으면 단맛이 처음에 느껴지고 서서히 박하같이 입을 시원하게 만든다. 단맛이 있어서 술이 좀 더 부드럽게 느껴지고 묵직하게 느껴진다. 목넘김은 매우 깔끔하다. 양념된 고기류와 잘 어울린다.',3,0,0,3,0,2,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(145,'매실향기 담은 술',14,6000,'국내산 쌀, 효모, 조효소재, 물엿, 아스파탐, 정제수','농업회사법인 명세주가(주)','충청북도 청주시 상당구 가덕면 한계길 32-5','충청북도 청주시','CHE','여름 과실인 매실이 들어간 만큼 상큼한 청량감이 느껴진다. 약주계열이지만 곡주 및 약재의 느낌은 거의 느껴지지 않는다. 다만 후미에서 느껴지는 곡물의 향은 있다. 잘 익은 매실을 청주에 담은 그 느낌 그대로다. 소고기 전골 등, 육류와 잘 어울린다.',3,3,3,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(146,'머루 이야기',12,6000,'국내산 산머루','농업회사법인산들벗(주)','전라북도 무주군 적상면 서창로 72','전라북도 무주군','WIN','연한 검붉은 빛의 색이며 다양한 과실향이 올라온다. 술을 입에 넣으면 달콤함이 입 안 전체에 퍼지고 금새 사라진다. 목넘김은 매우 깔끔하고 술을 마시고 난 후에 아주 약한 쓴맛이 혀에 자리를 잡는다. 잘 익힌 수육과 어울린다.',3,1,1,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(147,'머르드 서 드라이',13.5,35000,'국내산 산머루 100%','산머루농원영농조합법인','경기도 파주시 윗배우니길 441-25','경기도 파주시','WIN','오랜 숙성에서 이뤄지는 깊고 진한 검붉은 색의 와인빛을 띠고 있다. 향에서는 머루 특유의 시트러스 계열의 향부터 두터운 껍질에서 나오는 머루 껍질의 향도 즐길 수 있다. 와인에서 이야기하는 타닌감이 충분하다. 입안에 머금으면 신맛과 단맛, 그리고 오랜 기간 숙성을 통해 이뤄진 깊이 있는 맛의 밸런스가 잘 표현되어 있다. 음식으로는 고기류가 잘 어울리며, 진한 맛으로 양꼬치, 항정살, 스테이크와도 잘 어울리는 맛이다.',2,4,3,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(148,'머르드 서 스위트',12,30000,'국내산 산머루 100%','산머루농원영농조합법인','경기도 파주시 윗배우니길 441-25','경기도 파주시','WIN','맑고 진한 검붉은 색의 와인 빛을 띠고 있다. 향에서는 은은한 단맛과 시트러스 계열의 향이 좋은 밸런스를 유지하고 있다. 입안에 머금으면 신맛도 있지만, 적절한 단맛이 두터운 머루 와인의 맛을 편하게 만들어 준다. 다양한 맛의 조합이 특징인 만큼 음식으로는 불고기, 양념갈비, 떡갈비 등 양념이 들어간 고기류와 잘 어울린다.',4,3,3,4,0,0,0);
