insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(165,'무주 구천동 머루와인',12,25000,'국내산 산머루','덕유양조','전라북도 무주군 안성면 장무로 1375-7','전라북도 무주군','WIN','검붉은 빛의 묵직한 와인이다. 잘 익은 머루향과 산뜻한 발효향이 함께 올라온다. 입에 넣는 순간 달콤함과 자극적인 알코올맛이 입 안 전체를 채운다. 첫 맛은 달지만 천천히 올라오는 산미가 술 맛의 조화를 잘 이루게 한다. 술을 마시고 난 후에 미세한 떫은 맛이 식욕을 불러 일으킨다. 삼겹살과 잘 어울린다.',2,2,1,3,0,0,0);
