insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(167,'문경바람 백자 25도',25,30000,'사과(문경산)','농업회사법인(주)제이엘','경상북도 문경시 문경읍 새재로 609','경상북도 문경시','SPI','상큼한 증류주를 즐기고 싶은 사람에게 추천하는 제품. 옹기에서 숙성한 만큼 특유의 과실향이 살아있으며, 뒤이어 올라오는 증류주 특유의 타격감이 살아있다. 다서 터프한 느낌도 있지만 오히려 이러한 부분이 매력적으로 다가오는 제품. 전체적인 균형미와 사과향이 좋은 느낌이다. 하이볼 형태로 육전, 동태전, 수육 등과 잘 어울린다.',2,1,1,3,4,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(168,'문경바람 백자 40도',40,40000,'사과(문경산)','농업회사법인(주)제이엘','경상북도 문경시 문경읍 새재로 609','경상북도 문경시','SPI','말린 사과칩에서 나는 달콤한 사과향을 머금었다. 한 모금 맛보면 알코올감과 함께 산뜻한 느낌이 더해진다. 주인공 사과가 돋보일 수 있도록 사과 절임과 함께하면 좋겠다. 딜, 바질과 같은 허브를 곁들인다면 재밌는 경험이 될 것이다.',4,2,1,3,4,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(169,'문경바람 오크 40도',40,44000,'사과(문경산)','농업회사법인(주)제이엘','경상북도 문경시 문경읍 새재로 609','경상북도 문경시','SPI','사과의 상큼한 맛과 오크통에서 추출된 초콜릿, 바닐라, 아몬드 향이 묘하게 밸런스를 맞춘 제품. 40도의 고도주임에도 불구하고 새콤한 신맛을 느낄 수 있으며, 전체적인 향과 맛의 균형미가 좋고 약간의 단맛이 술을 맛있게 한다. 하이볼, 칵테일 등을 통해 식중주로도 충분히 즐길 수 있는 증류주. 초콜릿, 육포, 문경의 오미자 삼겹살구이, 더덕구이, 송이버섯구이 및 전골 등 추천',2,1,1,4,4,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(170,'문배술 헤리티지 23도',23,50000,'메조, 찰수수, 쌀','문배주양조원','경기도 김포시 통진읍 서암리 203-4','경기도 김포시','SPI','맑고 투명하며 익은 곡향이 올라온다. 알코올 도수가 높은 것은 아니지만 술 맛은 부드러워 알코올 도수가 더 낮은 숙성된 맛을 낸다. 자극적인 맛이 없어 누구나 편하게 즐길 수 있는 술이다. 첫맛과 뒷맛이 매우 깔끔하다. 복지리 등과 잘 어울린다.',1,0,0,1,0,2,0);
