insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(186,'바랑 탁',15,18000,'찹쌀(국내산), 맵쌀(국내산), 누룩, 소맥분, 정제수','농업회사법인 (주)금계당','경상북도 안동시 일직면 돌고개길 9, 1층','경상북도 안동시','TAK','진한 아이보리 빛깔의 술로 잘 익은 술에서 오는 캐러멜 맛이 입 전체를 감싼다. 단맛이 주를 이루지만 적절한 산미가 술의 느끼함을 없애주고, 찹쌀의 진한 여운이 오래 남는 술이다. 다른 막걸리에 비해 도수(15%)가 높아 음식먹을 때 약주잔에 한잔씩 하기 딱 좋은 술이다. 불고기가 생각나는 술이다.',4,2,1,4,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(187,'밤꽃향기',13.5,4000,'국내산 쌀, 밤, 누룩, 정제수','농업회사법인 사곡양조원','충청남도 공주시 사곡면 마곡사로 49','충청남도 공주시','CHE','잘 익은 밤에서 느껴지는 특유의 고소한 밤향이 특징이다. 초콜릿을 녹여 놓은듯한 질감이 밤향과 잘 어우러진 맛이다. 구운 밤하고도 잘 어울리며, 지역의 유명 음식인 공주국밥하고도 좋은 매칭이다.',5,2,2,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(188,'방문주 약주',15,7000,'국내산 찹쌀, 누룩, 정제수','케이소주 (전주가양주연구소)','전라북도 전주시 완산구 중산중앙로27. 3층','전라북도 전주시','CHE','옅은 황금색을 가진 방문주로 진한 과실향 보다는 신선한 곡물의 향이 떠오르는 느낌이다. 단맛은 배제되어 있지만, 후미에서 느껴지는 독특한 맛이 있다. 단맛이 적은 만큼 달콤한 음식과 잘 어울릴 듯하다. 배즙으로 듬뿍 들어간 한우 불고기, 잘 구워낸 떡갈비 등도 잘 어울리는 음식이다.',2,3,3,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(189,'방문주 탁주',15,6000,'국내산 찹쌀, 누룩, 정제수','케이소주 (전주가양주연구소)','전라북도 전주시 완산구 중산중앙로27. 3층','전라북도 전주시','TAK','잔잔한 아이보리 컬러를 가진 방문주 탁주는 과실향과 곡물향, 그리고 잘 익은 누룩향을 골고루 갖춘 밸런스 좋은 맛을 가지고 있다. 역시 특유의 단맛은 배제되어 있으며, 생주 특유의 산미를 가지고 있다. 식중주로도 좋은 역할을 할 듯하며, 잡채, 육전 등 기름진 요리와 잘 어울릴 듯하다.',2,3,3,4,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(190,'배꽃담은 연 생막걸리',10,20000,'국내산 쌀, 누룩, 효모, 젖산, 연잎, 정제수','(주)농업회사법인 술빚는 전가네','경기도 포천시 영북면 산정호수로 322번길 28','경기도 포천시','TAK','같은 양조장에서 나오는 동정춘과는 전혀 다른 맛과 향을 가지고 있다. 술 제조과정에 연잎을 넣은 술로 매우 신선한 향기를 갖고 있으며, 입 안에서 느껴지는 깔끔한 산미가 음식과 조화를 이루게 만들어 준다. 술을 마시고 난 후에 목넘김이 가벼워 누구나 편하게 마실 수 있는 막걸리이다. 낚지 회무침 등과 잘 어울린다.',3,3,4,2,0,0,0);
