insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(224,'블루베리 이야기',12,6000,'국내산 산머루 50%, 블루베리 50%','농업회사법인산들벗(주)','전라북도 무주군 적상면 서창로 72','전라북도 무주군','WIN','투명한 검붉은 빛을 가지고 있으며 신선한 베리향이 올라온다. 입에 넣으면 맛있는 단맛이 술 맛을 이끌어가며 산미는 매우 적다. 목넘김은 매우 부드럽고 술을 마시고 난 후에는 알코올의 진한 여운이 남는다. 숯불구이와 잘 어울린다.',3,1,1,3,0,3,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(225,'산내울 복분자주',16,20000,'복분자, 설탕, 주정, 정제수, 과당','거창사과원예농협','경상남도 거창군 거창읍 거함대로 3452-41','경상남도 거창군','SPI','오미자의 색보다는 옅은 옐로 다이아몬드의 색을 띠고 있다. 스치듯 보면 마치 사과주와 같은 색감으로 느껴진다. 하지만 향을 맡아보면 이 술의 반전이 시작된다. 오미자의 산뜻한 새콤함이 그대로 올라오기 때문. 입안에 머금으면 오미자의 다섯 가지 맛(단맛, 짠맛 신맛, 매운맛, 쓴맛)이 그대로 따라온다. 하지만 단맛과 신맛의 중심이 되어 있어 거부감은 전혀 느껴지지 않는다. 여름에 얼음과 탄산수를 넣고 마시면 더할 나위 없이 시원한 술이다.',4,4,3,3,0,0,0);
