insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(239,'샤토미소 스위트 로제',12,20000,'포도, 산화방지제, 효모, 설탕 등','도란원','충청북도 영동군 매곡면 유전 장척길 143','충청북도 영동군','WIN','술 빛이 붉고 맑은 것이 특징이며, 달콤한 베리향과 여름에 잘 익은 붉은 딸기향이 난다. 단맛이 강하지만 바디감이 가볍고 부드러운 목넘김이 좋다. 초콜릿과 잘 어울린다.',3,1,2,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(240,'샤토미소 영동',40,30000,'포도','도란원','충청북도 영동군 매곡면 유전장척길 143','충청북도 영동군','SPI','가볍게 오크통에서 숙성한 듯 황금색을 띠고 있다. 알코올 도수 40도의 높은 도수지만, 원액에서 묻어나오는 포도향과 가벼운 화이트 초콜릿 향이 느껴진다. 알코올 도수 40도를 생각하면 무척 부드러운 맛을 자랑하며, 마지막 후미에서 살짝 산미가 느껴지는 부분이 매력적이다. 기름진 메로구이 등이 생각나는 맛이다.',0,0,0,4,0,3,2);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(241,'샹스프랑보아즈',13,150000,'복분자(고창산), 구연산, 과당, 아스파탐, 주정, 정제수','(주)선운산복분자주 흥진','전라북도 고창군 부안면 복분자로535','전라북도 고창군','WIN','가벼운 느낌의 복분자 와인. 진한 복분자주보다는 산뜻한 로제와인과 같은 느낌이 든다. 적보라의 빛을 지니고 있고 상큼한 과일향이 난다. 담백한 모짜렐라치즈 등과 잘 어울린다.',4,3,3,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(242,'서울의 밤',25,7500,'매실증류주원액, 벌꿀, 포도당, 노간주나무열매, 정제수','더한주류','서울시 은평구 증산로7길 28-13','서울시 은평구','SPI','노간주나무열매가 들어간 진(GIN)의 느낌에 달콤한 향이 섞여 올라오는 것이 특징이다. 약간의 단맛이 술의 풍미를 좋게 만들고 입 안의 자극을 줄여준다. 입 안의 알코올 풍미가 가득하고 마시고 난 후에는 여운이 길게 남는다. 삼겹살과 아주 잘 어울린다.',0,0,0,2,0,4,1);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(243,'서주 16도',16,45000,'백미(국내산), 감자(국내산), 누룩, 효모, 정제수','오대서주양조','강원도 평창군 진부면 영정게길57-1','강원도 평창군','CHE','살짝 갈아놓은 감자에서 느낄 수 있는 식감이 있다. 맑은 연두 빛의 개성 있는 색깔에 담백한 단맛이 느껴진다. 너무 맵고 짠 음식은 자칫 감자술의 은은한 맛이 가려질 수 있기 때문에 자극적이지 않은 안주를 추천합니다. 기름진 감자전하고 즐기면 딱 좋은 맛이다.',3,2,3,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(244,'석로주',13,0,'멥쌀(국내산), 석이버섯(국내산), 정제수, 누룩(밀함유), 효모','석이원주조','대전광역시 동구 송촌남로 13','대전광역시 동구','CHE','은은한 국화향과 곡물향이 잘 섞여 올라오는 것이 특징이며, 단맛과 신맛이 적어 술이 매우 담백해 어떤 음식과도 잘 어울릴 만한 맛을 가지고 있다. 드라이한 맛이 입 안을 꽉 채우고 마시고 난 후에는 은은한 쓴맛이 혀를 자극시킨다. 버섯 전골 등 전골류와 잘 어울린다.',1,1,1,4,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(245,'석탄주 약주',15,7000,'국내산 찹쌀, 누룩, 정제수','케이소주 (전주가양주연구소)','전라북도 전주시 완산구 중산중앙로27. 3층','전라북도 전주시','CHE','옅은 황금색의 색을 가진 석탄주이다. 살짝 코끝을 스치는 시트러스 계열의 과실향이 다가오지만 이내 이 술이 추구하는 바가 무엇인지 알게 된다. 그것은 단맛이 전혀 없는 드라이함의 극치. 식중주가 잘 어울리는 약주로, 술의 향을 즐기기 위해 자극성 없는 음식과 함께 즐기는 것이 중요하다. 고기류라면 기름기가 적은 수육, 생선이라면 맛을 최대한 이끌어 내기 위해 참치 뱃살 등이 잘 어울릴 듯하다.',1,3,2,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(246,'석탄주 탁주',15,6000,'국내산 찹쌀, 누룩, 정제수','케이소주 (전주가양주연구소)','전라북도 전주시 완산구 중산중앙로27. 3층','전라북도 전주시','TAK','옅은 아이보리 컬러의 이 탁주는 부드러운 과실향을 간직하고 있다. 전체적으로 매끄러운 맛이 리딩을 하며 마지막에 상큼한 신맛이 다시 한번 입맛을 다시게 해 준다. 과실향이 좋아 식중주보다는 식전주로 어울리며, 술의 향을 방해하지 않는 가벼운 샐러드 등이 잘 어울릴 듯하다.',2,3,3,4,0,0,0);
