insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(248,'선운산 복분자주',16,25000,'복분자(고창산), 과당, 구연산, 아스파탐, 무수아황산','(주)선운산복분자주 흥진','전라북도 고창군 부안면 복분자로535','전라북도 고창군','WIN','진하지 않은 맑은 느낌의 색이 자연스럽게 느껴지며, 신맛이 주는 새콤함이 청량감을 더한다. 다재다능한 식중주라는 판단. 달콤함과 새콤함의 조합이 좋은 복분자주. 보양식을 생각한다면 장어구이가 잘 어울릴 것이며, 달콤한 맛이 있는 만큼 디저트주로도 좋다. 의외로 치즈와도 좋은 궁합. 다재다능한 식중주다.',4,4,3,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(249,'선운산 복분자주 프리미엄',19,40000,'복분자(고창산), 과당, 구연산, 아스파탐, 무수아황산','(주)선운산복분자주 흥진','전라북도 고창군 부안면 복분자로535','전라북도 고창군','WIN','18개월에 걸친 장기발효숙성으로 일반 복분자주에 비해 부드러움이 느껴진다. 자극이 적은 새콤한 맛에 매끈한 단맛이 숙성이 주는 매력을 느낄 수 있다. 담백한 한우 수육을 곁들인다면 더욱 그 차이를 알 수 있을 것이다.',4,3,3,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(250,'선운산의 아침 복분자막걸리',6,28000,'복분자, 쌀, 효모, 국, 과당, 구연산, 젖산, 정제수','(농)국순당고창명주(주)','전라북도 고창군 심원면 선운대로 2197','전라북도 고창군','TAK','은은하게 올라오는 익은 베리향이 일품이며 단맛과 신맛의 조화가 잘 어울어져 있다. 적절한 산미가 입 전체를 깔끔하게 만들어주고 술을 마시고 난 후에 혀에 남는 잔맛이 없어 전체적으로 가겹게 느껴진다. 양념을 강하게 하지 않은 생선찜과 잘 어울린다.',2,2,1,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(251,'선호 생막걸리',6,1700,'국내산 쌀, 누룩, 효모, 정제효소, 천연감미료, 정제수','김포금쌀탁주영농조합법인','경기도 김포시 하성면 하성로 622','경기도 김포시','TAK','쌀 특유의 풍미가 살아있는 막걸리. 개운한 맛으로 다양한 음식과 잘 어울리는 스타일. 칼칼한 맛이 살아있어 다양한 음식과 식중주로 잘 어울리는 막걸리이다. 단맛이 적어 달콤하고 매운 음식과 잘 어울릴 수 있다. 닭도리탕, 감자탕 등도 추천 메뉴다.',2,3,3,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(252,'설궁',30,0,'자연산 송이버섯, 산양산삼, 토종벌꿀','솔래원','강원도 양구군 방산면 칠전길 12-7','강원도 양구군','ETC','은은하게 버섯향과 삼향이 올라오고 술을 입에 넣으면 알코올도수에 비해 도수가 낮아 보일 정도로 부드럽다. 인위적인 맛과 향은 있으나 거부감이 없고 누구나 편하게 즐길 수 있는 술이다. 조개구이와 잘 어울린다.',2,0,0,2,0,2,0);
