insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(254,'설련주',16,35000,'백련, 찹쌀, 멥쌀, 누룩','영농조합법인 석전상온전통주가','경상북도 칠곡군 왜관읍 동산2길19','경상북도 칠곡군','CHE','기분 좋은 담황색으로 시작하는 약주다. 부드러운 산미가 입속을 감싸주며, 이내 쌀 향이 주는 매끈한 단맛으로 후미를 장식한다. 가벼운 누룩향이 있는데, 숙성된 장을 느끼는 듯한 진한 맛이 살아있다. 한복을 입고 마시면 가장 잘 어울릴 듯한 격식을 갖췄으며, 명절 때 차례주, 또는 제사주로 사용하는 것을 추천한다. 잘 어울리는 안주는 다소 진한 맛이 있는 만큼 진한 안주가 잘 어울리며, 주로 간장 맛이 좋은 불고기, 간장 게장, 궁중 떡볶이 등을 추천한다. 연잎이 들어간 만큼 연잎밥과도 좋은 매칭이다.',2,4,4,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(255,'설성 만월 막걸리',8,4000,'정제수, 유기농쌀국내산, 누룩, 유기농올리고당 등','병영주조장','전라남도 강진군 병영면 하멜로 407(성남리 140-3)','전라남도 강진군','TAK','고소한 곡물 볶는 향이 있고 단맛과 신맛의 균형이 잘 갖춰져 있으며, 술이 가볍지 않고 묵직한 목넘김이 특징이다. 약간의 떫은맛도 가지고 있다. 새우 간장 절임과 잘 어울린다.',2,3,2,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(256,'설하담',8,7400,'국내산 멥쌀 100%, 개량 누룩, 정제수, 이산화탄소','리큐랩주식회사','부산광역시 영도구 남항새싹길 186','부산광역시 영도구','TAK','설하담은 누구나 맛있게 즐길 수 있는 술이다. 플라스틱 용기를 사용하지만 기존 막걸리 용기와는 다른 젊은 사람들이 매우 좋아할 만한 디자인을 적용하였다. 감미료가 들어가 있지만 인위적이지 않으며, 술을 마시고 난 후에 잔여 향기가 입 안에 오래 남는데 그 느낌이 매우 매력적이다. 양념된 돼지고기와 잘 어울린다.',3,1,4,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(257,'성포 생막걸리',6,1100,'쌀(국내산), 소맥분(미국산), 전분당, 정제수, 무농약 미나리추출액, 정제효소, 종국, 효모, 아스파탐','성포양조장','경상남도 거제시 사등면 지석3길3','경상남도 거제시','TAK','진한 아이보리색깔에 오일장 뻥튀기집에서 나오는 고소한 향이 매력적인 술이다. 막걸리는 기본적으로 식이섬유가 많은데 여기에 미나리추출액이 들어가 다이어트 막걸리로 손색이 없다. 전체적으로 단맛과 신맛의 조화가 잘 갖춰져 있으며 마시고 난 후에 술의 산미가 입 전체를 산뜻하게 만들어 준다. 미나리전이나 무침과도 잘 어울린다.',3,3,3,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(258,'세우리',45,250000,'산양삼, 하수오, 구기자, 쌀','제주샘영농조합법인','제주특별자치도 제주시 애월읍 애원로 283','제주특별자치도 제주시','SPI','유리병 안에 들어있는 산양산삼이 인상적이고 진한 호박색의 술로 인삼과 함께 여러 가지 약재의 향이 가득한 것이 특징이다. 도수가 높아 집에 두고 한 잔씩 약으로 마시기 좋은 술이며, 입 안의 풍미가 약재의 향으로 가득하고 목 넘김이 뜨겁다. 삼계탕 등 몸 보양을 위해 먹는 음식과 다 잘 어울린다.',0,0,0,4,0,5,3);
