insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(273,'송명섭 막걸리',6,3000,'국내산쌀, 곡자, 정제수','태인합동주조','전라북도 정읍시 태인면 태흥리 392-1','전라북도 정읍시','TAK','송명섭 막걸리는 빚는 이를 닮아 소박한 맛이 난다. 어떠한 첨가물로 넣지 않았기 때문에 대중적인 막걸리와는 달리 단맛과 신맛이 거의 느껴지지 않는다. 입맛을 사로잡는 달달함은 없지만 마시고 나서 입안을 개운하게 한다. 그렇기 때문에 계속해서 마셔도 질리지 않는 막걸리이다. 맵고 짠 한식이나 기름진 고기류와의 음식 궁합이 좋다.',1,3,3,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(274,'송이주',13,12000,'국내산 쌀, 송이농충액, 누룩, 정제수','내국양조','충청남도 논산시 연무읍 마종로 361','충청남도 논산시','CHE','술 이름과는 다르게 신선한 레몬 등의 시트러스 향이 매우 강하게 올라오고 여기에 연하게 버섯향이 섞여있는 것이 특징이다. 전체적으로 산미가 중심을 잡고 미세하게 후추 맛이 섞여 있어 매우 독특하다. 술을 마시고 난 후에도 술이 무겁지 않고 가벼워 편하게 마실 수 있는 술이다. 소 등심 구이랑 잘 어울린다.',1,4,1,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(275,'송화백일주',38,45000,'쌀, 찹쌀, 누룩, 산수유, 오미자, 구기자, 솔잎, 송화가루, 꿀','송화양조사','전라북도 완주군 구이면 구이로 1096','전라북도 완주군','SPI','마치 소나무 아래 앉아 있는 듯 솔향이 산뜻하게 은은하게 올라온다. 38%의 알코올 성분을 가지고 있지만 매우 부드럽고 술을 마시면 송화, 구기자 등의 약재 맛이 입 안을 꽉 채워준다. 집에 두고 하루 한 잔 하기 아주 좋은 술이다. 마실 때 얼음에 넣어 차게 마시고 삼계탕 등 보신 음식들과 아주 잘 어울린다.',0,0,0,3,0,3,3);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(276,'수삼단본 23도',23,32000,'쌀, 인삼, 누룩, 정제수 등','농업회사법인(주)금산인삼주','충청남도 금산군 금성면 파초길 23','충청남도 금산군','SPI','맑고 투명한 증류주에 인삼이 들어있어 술을 따르면서 아주 신선한 인삼향기가 올라온다. 입에 넣으면 알코올이 입술과 혀를 자극시키고 서서히 인삼의 쓴맛이 기분 좋게 올라온다. 역시 인삼주는 삼계탕과 함께 해야 제 맛이 난다.',1,0,0,3,0,4,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(277,'수삼단본 43도',43,42000,'쌀, 인삼, 누룩, 정제수 등','농업회사법인(주)금산인삼주','충청남도 금산군 금성면 파초길 23','충청남도 금산군','SPI','기본적인 맛은 금삼인삼주 25%와 동일하나 43%짜리가 알코올 도수가 높아 입에 넣으면 알코올이 입술과 혀를 자극시키는 맛이 강한다. 23%짜리가 도수가 낮아 편하게 즐길 수 있는 술이라면 43%짜리는 도수가 높아 음식과 함께 반주 정도로 먹으면 좋을 만한 술이다. 오리 백숙과 잘 어울린다.',1,0,0,4,0,4,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(278,'순향주',15,56000,'국내산 쌀, 누룩, 정제수','(주)농업회사법인 추연당','경기도 여주시 가남읍 금당리길 1-111','경기도 여주시','CHE','잘 익은 황금빛 논밭을 떠올리는 색. 진한 향은 아니지만, 잘 익은 사과 향이 살짝 느껴진다. 전체적인 맛은 천편일률적인 단맛보다는 곡물이 주는 그윽함과 샤프하게 떨어지는 깔끔함이 이 술의 장점이다. 단맛이 적어 식중주로 최고의 역할을 할 듯하며, 한정식 등을 즐기는 자리에 추천하고 싶다.',2,2,2,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(279,'술공방 9.0 생막걸리',9,5000,'쌀(국내산), 밀누룩(국내산), 국, 효모, 효소, 올리고당','두이술공방','충청남도 청양군 대치면 방죽골길 46-19','충청남도 청양군','TAK','진한 아이보리 빛깔을 갖고 있으며 입 안에 넣으면 호두나 땅콩등 견과류를 혼합한 크림을 마시는 듯 하다. 입 안에 남는 고소한 여운과 쓴맛이 다른 막걸리와 확연히 구별된다. 단맛과 신맛의 조화가 잘 갖춰져 있고 고기류보다는 신맛이 곁들여져 있는 무침류와 잘 어울린다.',3,2,1,3,0,0,0);
