insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(290,'스타베리오디',12,28000,'양조용포도(경북 영천)','(주)한국와인','경상북도 영천시 금호읍 창산길 100-44','경상북도 영천시','WIN','오디 특유의 산미과 단맛이 그대로 살려져 있는 와인. 한국산 오디와인의 절정을 느낄 수 있는 맛이다. 3년 숙성의 세월이 느껴지는 묵직한 맛이 있으며, 이러한 맛과 밸런스를 맞춰 줄 자극성 적은 나물 요리 등을 추천한다.',3,4,3,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(291,'시나브로 드 글로리아',12,25000,'포도(캠벨얼리)','불휘농장','충청북도 영동군 심천면 약목2길26','충청북도 영동군','WIN','맑고 청아한 포도색을 가진 와인이다. 단맛보다는 산미에 중점을 둔 와인이며, 향에서 묘한 뿌리 냄새가 나는 것도 특징이다. 물같이 부드러운 목 넘김을 가지고 있으며, 가벼운 탄닌감도 있어서 불고기 및 육전 등, 가벼운 고기류하고는 잘 어울릴 수 있다.',3,3,2,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(292,'시나브로 레드 드라이',12,25000,'포도(캠벨얼리)','불휘농장','충청북도 영동군 심천면 약목2길26','충청북도 영동군','WIN','맑은 포도의 색 자체를 그대로 보여주는 와인이다. 이름 그대로 단맛이 적은 드라이한 맛을 그대로 가지고 있지만, 부드러운 목 넘김을 가지고 있어서 밸런스가 잘 맞는다고 평할 수 있다. 후미는 길지 않지만, 오히려 이런 부분이 깔끔하게 떨어진다고 평하는 전문가도 있다. 아귀찜, 꽃게무침 등 다소 매운 맛이 있는 해산물과 즐긴다면, 매운맛을 균형 있게 잡아줄 것이라고 보인다.',2,3,3,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(293,'시나브로 레드 스위트',12,25000,'포도(캠벨얼리)','불휘농장','충청북도 영동군 심천면 약목2길26','충청북도 영동군','WIN','레드 와인보다 로제 와인으로 보일 만큼 청아하고 투명한 색을 가지고 있다. 캠벨 특유의 기분 좋은 단맛이 맛을 이끌며, 후미에서도 단맛이 남지 않아 청량감도 느껴지는 와인이다. 누가 즐겨도 편안하고 무난하게 즐길 수 있는 맛이며, 달콤한 맛이 특별한 만큼 좋은 식사를 기분 좋게 마무리하는 역할로 식후주로 추천하고 싶다.',3,3,3,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(294,'시나브로 로제와인',10,30000,'포도(국내산_캠벨얼리)','불휘농장','충청북도 영동군 심천면 약목2길26','충청북도 영동군','WIN','잔잔한 포돗빛을 띈 로제 와인이다. 우리가 자주 접하는 캠벨얼리의 풍부하고 상큼한 향으로 시작을 하며, 입안에 머금는 순간 부드러운 단맛이 기억에 많이 남는다. 전체적으로 매운맛, 쓴맛, 그리고 타닌감은 없지만, 식전 주에는 식욕을 돋우는 와인으로, 식후에는 입속에 좋은 향기를 느끼게 하는 와인으로는 적합한 와인으로 볼 수 있다. 산미가 적은 부드러운 좋은 와인인 만큼 드레싱을 뿌린 샐러드와도 잘 어울리며, 새우튀김 등과 함께 즐겨도 좋다.',3,3,3,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(295,'시나브로 에뚜왈 로제 스파클링',5,30000,'포도(국내산_캠벨얼리)','불휘농장','충청북도 영동군 심천면 약목2길26','충청북도 영동군','WIN','연 붉은색을 가진 로제 스파클링 와인이다. 화려한 향은 아니지만 살짝 마른나무 향이 느껴진다. 부드러운 기포와 함께 올라오는 맛은 달콤한 맛으로 시작하지만 신맛과 잘 발효된 누룩향도 느껴진다. 전체적으로 식전 주 또는 식후 주가 맞는 와인으로 스위트한 맛이 주축을 이룬 만큼 샐러드와 같은 가벼운 요리가 어울릴 것이다.',2,3,4,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(296,'시나브로 에뚜왈 스파클링와인',5,30000,'포도(청수)','불휘농장','충청북도 영동군 심천면 약목2길26','충청북도 영동군','WIN','산뜻한 레몬색을 가지고 있는 스파클링 와인이다. 과하지 않은 단맛과 산미가 맛의 축을 담당하고 있으며, 알코올 도수가 5도로 낮은 만큼 좋은 맥주 대용으로 즐기기에도 좋은 와인이다. 청량감 좋은 탄산으로 기름진 육류와도 잘 어울린다.',3,3,4,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(297,'시나브로 화이트와인',11,30000,'청포도(청수)','불휘농장','충청북도 영동군 심천면 약목2길26','충청북도 영동군','WIN','아름다운 골드빛을 가진 화이트 와인이다. 첫 향부터 밸런스가 잘 이뤄진 청포도향은 물론 사과향, 복숭아향까지 같이 느껴진다. 맛에서는 입맛을 자극하는 새콤함부터 마지막에 감미로운 느낌을 주는 달콤함도 품고 있다. 타닌감 및 오크통에서 나오는 진한 맛보다는 좋은 청포도를 맑은 곳에서 잘 발효시켰다는 이미지다. 단맛이 다소 있지만, 산미가 좋아 식중주로도 가능하다. 다만 육류보다는 자극성이 적은 흰살생선을 추천하며, 이왕이면 고추장, 된장으로 요리된 것보다는 간장 베이스로 옅은 양념의 음식이 좋을 것이다.',4,4,4,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(298,'시인의 마을 우리쌀 막걸리',10,7900,'쌀(국내산), 누룩, 정제수, 무감미료','이원양조장','충청북도 옥천군 이원면 묘목로 113','충청북도 옥천군','TAK','묵직한 막걸리 원액과 같은 느낌으로 진한 쌀 향이 올라온다. 얼음을 타서 온더록으로 마시는 것도 추천한다. 밥과 같은 쌀막걸리인 만큼 금강에서 잡히는 민물고기 매운탕과 같이 먹는 것이 좋다. 물론 밀 막걸리 만큼은 아니지만 원액에 가까운 만큼 묵직한 맛을 자랑하니, 식감이 가벼운 도토리묵, 겉절이, 나물 등도 좋다.',2,3,3,4,0,0,0);
