insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(303,'신선주',16,19000,'쌀, 능금, 신선초, 천궁, 구기자, 당귀솔잎, 대추, 인삼','용두산조은술','충청북도 제천시 송학면 송학주천로647','충청북도 제천시','CHE','아주 맑은 황금빛의 술이며 당귀 등과 곡물에서 나오는 약재향과 고소함이 잘 올라온다. 단맛과 신맛이 잘 갖춰져 있으며 처음 입 안에 들어오는 센맛이 독특하다. 첫맛은 강하지만 시간이 지나면 가볍게 사라진다. 알코올의 맛이 술을 마시고 난 후에도 오래 머무른다. 진한 불고기와 잘 어울린다.',3,3,2,4,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(304,'심플리 애플',12,9000,'쌀(국산), 사과즙(국산), 벌꿀(국산)','주식회사 배혜정 농업회사법인','경기도 화성시 정남면 서봉로 835','경기도 화성시','ETC','맑고 투명한 옅은 노란빛을 띈다. 상큼한 사과의 향과 벌꿀의 달콤한 향을 맡은 후 한 모금 마시면, 달콤한 맛이 혀를 감싼다. 도수에 비해 목 넘김은 부드러운 편이다. 알코올 도수가 높지 않아 부담스럽지 않고, 편안하게 즐길 수 있는 술이다. 함께 먹기에 어울리는 음식으로는 광어회, 냉채족발, 참소라가 있다.',4,4,1,2,3,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(305,'아이싱 자몽(iCing Grapefruit)',4,1300,'쌀, 자몽엑기스','국순당','강원도 횡성군 둔내면 강변로 974','강원도 횡성군','TAK','붉은 진주빛을 가지고 있으며 잘 익은 검은 과실향이 올라온다. 술을 입에 넣으면 단맛이 가장 먼저 올라오고 그 다음 산미가 뒤따라 온다. 목넘김은 가볍고 깔끔하다. 술을 마시고 난 후에 미세하게 남는 쓴맛과 알코올맛을 느낄 수 있다. 케익 등 디저트 음식과 잘 어울린다.',3,1,1,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(306,'아이원 막걸리',6,1000,'쌀, 밀, 입국, 누룩, 정제수','이원양조장','충청북도 옥천군 이원면 묘목로 113','충청북도 옥천군','TAK','경쾌함과 발랄함, 그리고 생 특유의 신선함이 그대로 느껴지는 막걸리로 이원 양조장의 막걸리 중에서 대중적인 맛에 속한다. 옥천의 금강 상류에서 잡히는 빙어요리인 도리뱅뱅, 그리고 민물 매운탕과 같이 먹는 것이 가장 좋다. 묵직한 막걸리인 만큼 식감이 가벼운 도토리묵, 겉절이, 나물 등과 잘 어울린다.',3,3,3,2,0,0,0);
