insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(324,'어우야',16,9900,'야관문(제주산), 정제수, 포도당, 효모','왕지케양조장 (제주본초협동조합)','제주특별자치도 제주시 한경면 고산로 2길 22-13','제주특별자치도 제주시','ETC','어우야  네이밍의 느낌과 같이 야관문을 넣어 빚은 발효주이다. 야관문의 독특한 맛이나 향 보다도 야관문이 가지고 있는 약재의 효능을 잘 살린 술이라 할 수 있다. 미색을 가지고 있으며 입 안에 들어가면 약재 맛이 가득하지만 느낌이 매우 산뜻하다. 저녁 식사와 항상 함께하기 좋은 술이다.',0,0,0,2,0,2,3);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(325,'여강',25,20000,'국내산 쌀, 누룩, 정제수','(주)농업회사법인 추연당','경기도 여주시 가남읍 금당리길 1-111','경기도 여주시','SPI','증류식 소주 특유의 투명한 색이다. 잘 구워진 곡물의 향이 있으며, 혀끝에서 목넘김으로 이어지는 과정이 거리낌없이 부드럽게 넘어간다. 잔잔한 향이 여운으로 계속 남는데 오히려 더욱 매력적으로 느껴진다. 갈비찜과 같은 진한 음식과 잘 어울린다.',0,0,0,3,0,3,2);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(326,'여포의 꿈 (레드 드라이)',12,20000,'국내산 포도 100%','여포와인농장','충청북도 영동군 양강면 유점지촌길 75(여포와인농장)','충청북도 영동군','WIN','신선한 포도 껍질에서 나는 향이 진하게 올라오고 단맛과 신맛의 균형이 잘 갖춰져 있다. 옅은 타닌에 의한 떫은 맛이 혀를 자극시키며 목넘김이 가볍다. 스테이크와 잘 어울린다.',2,2,2,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(327,'여포의 꿈(레드스위트)',12,19000,'국내산 포도 100%','여포와인농장','충청북도 영동군 양강면 유점지촌길 75(여포와인농장)','충청북도 영동군','WIN','여름에 잘 익은 포도밭에 온 듯한 향기와 산미는 적지만 풍만한 단맛이 입에 오랜 여운을 남긴다. 바디감이 있지만 목넘김이 부드럽다. 특별한 날 케익과 잘 어울린다.',4,1,3,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(328,'여포의 꿈(화이트)',12,20000,'국내산 포도 100%','여포와인농장','충청북도 영동군 양강면 유점지촌길 75(여포와인농장)','충청북도 영동군','WIN','신선한 포도향에 마치 포도농장에 와 있는 듯 하다. 단맛은 적고 산미가 적당히 있어 바디감이 적고 목넘김이 깔끔하다. 조기찜과 잘 어울린다.',1,2,3,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(329,'연천 아주',8,6000,'쌀(국내산), 누룩, 정제수','농업회사법인 연천양조(주)','경기도 연천군 마산면 청정로 1738번길 15','경기도 연천군','TAK','굵직한 아이보리 컬러가 전형적인 좋은 막걸리의 색을 가지고 있다. 코끝에 대면 향은 적게 올라오는데, 옅은 사과향과 잘 익은 단감의 향이 스치듯 지나간다. 입에 머금은 순간 잘 익은 레몬이 주는듯한 산미가 살아있으며, 경쾌함과 산뜻함도 그대로 느껴지며 마지막에는 살짝 쌀 특유의 단맛도 살아있다. 지역이 연천인 만큼 임진강에서 잡히는 민물고기 매운탕과 함께 즐긴다면, 매운탕 특유의 매운맛을 연천 아주가 잘 잡아줄 듯하다.',2,4,3,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(330,'연천 연주',12,15000,'쌀(국내산), 누룩, 정제수','농업회사법인 연천양조(주)','경기도 연천군 마산면 청정로 1738번길 15','경기도 연천군','CHE','기분 좋은 황갈색을 띤 무감미료 수제 약주. 은은한 누룩향이 먼저 피어오르는 첫인상을 가지고 있다. 입에 머금으면 기분 좋은 산미와 단맛이 조화를 이루며 침샘을 자극한다. 마무리는 부드러운 누룽지향. 산미가 도드라진 만큼 강한 맛보다는 담백한 음식이 잘 어울릴 것으로 보인다. 삼겹살, 양념갈비보다는 수육 및 육회 등이 좋을 듯하며, 김치전보다는 해물전이 더 잘 어울릴 듯하다.',2,4,2,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(331,'연천 우주',22,8500,'쌀(국내산), 누룩, 효모, 효소, 연천율무, 정제수','농업회사법인 연천양조(주)','경기도 연천군 마산면 청정로 1738번길 15','경기도 연천군','SPI','무색투명한 색으로 시작한 이 소주의 매력은 향에서 만날 수 있다. 잘 구워진 가래떡과 같은 고소함이 있기 때문. 묵직한 알콜올 맛에서 시작했다가 이내 입속을 가득 채우는 쌀 발효향을 느낄 수 있다. 알코올 함유량 22%로 다른 증류식 소주에 비해 다소 낮은 편이지만, 향 하나하나가 무척 잘 살려져 있어 얼음, 탄산수 등과 함께 즐기기 충분하다. 얼음이나 탄산수 없이 단독으로 즐긴다면 육포 및 견과류를 추천하며, 고기류라고 한다면 담백한 한우 등심구이 등이 잘 어울릴 것이다.',0,0,0,3,0,3,2);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(332,'영일만친구 막걸리',6,15000,'국내산 쌀, 물엿, 우뭇가사리, 효모, 조제 종국, 정제효소제, 아스파탐, 젖산','동해명주','경상북도 포항시 남구 동해면 일월리 51-1','경상북도 포항시','TAK','동해명주에서 나오는 동해동동주, 도구 막걸리 그리고 영일만 친구는 비슷한 맛을 가지고 있지만 특징이 하나씩 있다. 동해동동주는 쌀알이 동동 뜨는 모습, 도구 막걸리는 쌀과 함께 밀가루를 첨가하였으며, 영일만 친구는 우뭇가사리를 넣은 술이다. 우뭇가사리의 풍부한 식이섬유를 함께 마시는 즐거움이 있다. 역시 우뭇가사리 무침과 잘 어울린다.',2,2,3,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(333,'예담은 한산소곡주',16,12000,'찹쌀, 멥쌀, 누룩, 대두, 구절초','한산예담은소곡주','충청남도 서천군 한산면 한마로 5(동산리)','충청남도 서천군','CHE','진한 담황색의 술이다. 술의 단맛이 술이 오래 익었을 때 나오는 캐러멜 향과 잘 섞여 있다. 알코올의 자극적인 맛이 식도를 타고 식도를 흐른다. 술을 마시고 난 후에 입 안에 남는 섬세한 쓴맛이 좋다. 일상에서 먹는 반찬과 잘 어울린다.',4,1,1,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(334,'예담은 한산소곡화주',41,30000,'찹쌀, 멥쌀, 누룩, 대두, 구절초','한산예담은소곡주','충청남도 서천군 한산면 한마로 5(동산리)','충청남도 서천군','SPI','높은 온도에서 증류한 곡향이 올라오고 알코올 도수에 비해서 술 맛이 매우 부드럽다. 술을 마시고 난 후에 혀에 남는 알코올이 긴 여운을 남긴다. 시간이 지날수록 박하와 가은 화한 맛이 입 안에 남는 것이 특징이다. 맑은 복지리 탕과 잘 어울린다.',3,0,0,3,0,2,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(335,'예밀와인 레드 드라이',13,20000,'포도(캠벨얼리)','예밀2리영농조합법인','강원도 영월군 김삿갓면 예밀촌길 228-20','강원도 영월군','WIN','붉은 흑진주의 색을 닮은 예밀와인은 드라이와 스위트 두 가지 맛이 함께 출시되고 있다. 드라이는 맛과는 다르게 붉은 벨리류에서 나는 달콤한 딸기향이 가득해 향만으로도 사람의 입맛을 자극한다. 그러나 입 안에 들어가면 드라이  버전 답게 매우 담백하고 단맛이 적어 양념이 되지 않은 구은 고기류의 음식과 함께 마시기 좋다.',1,1,2,3,0,0,0);
