insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(346,'오디랑',13,4000,'오디(국산) 18.2%, 정제수, 과당, 주정, 구연산, 효모','국순당 고창명주','전라북도 고창군 심원면 선운대로 2197','전라북도 고창군','WIN','검붉은 색을 가지고 옅으면서 달콤한 과실향이 올라온다. 첫맛은 달콤하고 그 뒤로 약한 쓴맛이 입 전체에 자리를 잡는다. 알코올의 맛이 입과 식도를 뜨겁게 하지만 먹고 난 후 입 안은 깔끔하다. 양념된 고기류와 잘 어울린다.',3,1,1,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(347,'오매락퍽',40,52000,'구운매실, 배증류원액','(주)배상면주가','경기도 포천시 화현면 화동로 432번길 25','경기도 포천시','SPI','맑은 담황색의 술로 알코올에 잘 익은 과실향이 실려 올라온다. 술을 입에 넣으면 달콤함이 처음 느껴지고 뒤로 진한 알코올 맛이 느껴진다. 푹 익은 매실의 맛과 알코올도수에 비해 입 안에 알코올의 퍼짐이 강하고 뜨거운 열기가 목에 오래 남아 있다. 입이 즐거운 술이다. 신선한 육회와 함께하면 좋은 술이다.',3,0,0,4,0,3,0);
