insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(359,'우도 땅콩 전통주',6,4000,'정제수, 전분당, 백미, 국, 땅콩분, 효모, 젖산 등','조은술 세종','충청북도 청주시 청원구 사천로 18번길 5-2','충청북도 청주시','ETC','방앗간에서 깨소금을 볶았을 때 나는 고소한 향기를 가지고 있으며, 산미가 적고 단맛이 강하지만 목넘김은 매우 깔끔하다. 입 안에 기름진 맛이 특징이다. 안주 없이 들이키기 좋은 술이다.',4,1,3,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(360,'운암1945',12,28000,'쌀(국내산), 누룩, 정제수','농업회사법인 우포의아침 주식회사','경상남도 창녕군 대지면 대지농공단지길 40','경상남도 창녕군','CHE','산도, 당도, 보디감의 밸런스가 아주 좋아 섬세하고 우아한 느낌이다. 한치, 새우와 같은 해산물, 알리오올리오 파스타, 파마산치즈, 체다치즈와 함께 즐기기 좋다.',3,3,3,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(361,'원매 프리미엄',20,45000,'매실주원액, 발효주정, 정제수, 꿀','더한주류','서울시 은평구 증산로7길 28-13','서울시 은평구','SPI','맑은 담황색의 빛깔을 가지고 있으며 은은하고 깊게 매실향이 올라온다. 단맛이 신맛보다 강하지만 맛의 조화가 잘 갖춰져 있다. 처음에는 단맛이 느껴지지만 그 뒤로 산미가 올라와 입 전체에 침이 고이게 만든다. 목넘김 또한 매우 부드럽다. 파스타 등 면요리와도 잘 어울린다.',2,0,0,4,0,3,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(362,'원매',15,35000,'매실원주액, 매실청, 꿀, 카라멜, 효소처리스테비아, 정제수','더한주류','서울시 은평구 증산로7길 28-13','서울시 은평구','SPI','황매실을 이용해 맑은 호박색을 가지고 있으며, 매실의 신선한 향이 가득한 것이 특징이다. 도수가 낮아 누구라도 편하게 즐길 수 있으며 처음 단맛이 올라오지만 뒤에 따라오는 신맛과 적절한 쓴맛의 조화가 잘 갖춰진 술이다. 입 안의 느낌이 매우 따뜻하고 마시고 난 후에는 여운이 길다. 숙주 소고기 볶음 등 약간의 양념이 된 깔끔한 음식과 잘 어울린다.',0,0,0,3,0,4,1);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(363,'원조 한산소곡주',16,12000,'찹쌀, 멥쌀, 누룩, 대두, 구절초','원조소곡주','충청남도 서천군 한산면 한마로34','충청남도 서천군','CHE','소곡주는 일명 앉은뱅이 술 로 도수가 높지만 달콤해 상대적으로 순하고 부드러운 맛이 특징이다. 맑은 호박색을 가지고 있고, 진한 단맛이 매력적이며 입 안에 감도는 풍미가 술을 마시고 난 후에도 오래 남는다. 마실 때 온도를 낮춰(10도 정도) 마시는 것을 추천한다. 모시떡과 아주 잘 어울린다.',3,1,4,2,0,0,0);
