insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(376,'이도 32',32,37000,'물, 증류원액(유기농 쌀, 국내산)','조은술세종','충청북도 청주시 청원구 사천로18번길 5-2','충청북도 청주시','SPI','무색투명하며 곡물향보다는 깔끔한 느낌이 주를 이룬다. 감압식 증류 방식이 누룩향을 잡아줘 깔끔하고 담백하다. 레몬, 라임과 탄산수를 섞어 마시거나 커피리큐르와 섞는 등 다양한 칵테일로 즐기기 좋다. 함께 먹기에 어울리는 음식으로 복지리탕, 우수육, 어복쟁반이 있다.',2,1,3,2,3,2,3);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(377,'이동 생 쌀 막걸리',6,1300,'정제수, 백미, 팽화미, 입국, 아스파탐 등','이동주조','경기도 포천시 이동면 화동로 2466','경기도 포천시','TAK','쌀과 쌀 누룩을 이용해 막걸리 빛이 희고 팽화미 특유의 고소한 맛과 향미가 있는 것이 특징이다. 적절한 산미가 음식맛을 도드라지게 한다. 갈비찜과 어울린다.',1,3,3,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(378,'이동 쌀 막걸리',6,1600,'정제수, 백미, 팽화미, 입국, 아스파탐 등','이동주조','경기도 포천시 이동면 화동로 2466','경기도 포천시','TAK','맑은 크림을 먹는 듯 하고 단맛은 있으나 신맛의 거의 없다. 전체적으로 입안에 오래 남는 묵직함을 가지고 있는 술이다. 도토리 묵과 잘 어울린다.',3,1,1,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(379,'이상헌 약주',18,45000,'아산쌀, 국(국내산 밀누룩), 정제수','이가수불','충청남도 아산시 도고면 기곡로 77번길 30-7','충청남도 아산시','CHE','묵직하지만 결코 부담스럽지 않는 맛. 진한 곡물의 향이 있지만, 단맛과, 신맛 등 맛의 조화가 잘 이뤄졌다. 알코올 도수가 18도나 되지만, 실질적으로는 고급술이 주는 부드러움도 가지고 있다. 묵직한 곡물의 맛이 느껴지고 목 넘김이 좋아 홀짝홀짝 마시게 된다. 조개탕이나 꼬막무침과 마시기 좋다.',3,3,3,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(380,'이상헌 탁주',19,25000,'아산쌀, 국(국내산 밀누룩), 정제수','이가수불','충청남도 아산시 도고면 기곡로 77번길 30-7','충청남도 아산시','TAK','탁주임에도 불구하고 살구 향, 복숭아 향 등 농익은 과실향이 나타난다. 단맛은 적지만, 다양한 과실향과 잘 발효된 시트러스한 식감으로 좋은 밸런스를 낸다. 탁주 자체의 맛을 음미할 수 있는 자극성 적은 샐러드류와 함께 즐기는 것을 추천해 본다.',2,3,3,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(381,'이화백주',6,12000,'국내산 햅쌀, 전통 누룩, 정제수','이화백주 순탁주','경상남도 양산시 상북면 충렬로 941','경상남도 양산시','TAK','용해되었던 탄산의 경쾌함과 레몬 맛과 같은 시트러스 계열의 맛과 향. 행사 및 파티용 술로 최적이다. 탄산으로 인해 단맛은 적게 느껴지며 청량감은 최고조를 이룬다. 맥주 대신 첫술을 시작하는 용으로 가장 좋으며, 기름진 음식과 잘 맞는다. 구체적으로는 갈비찜, 해물파전, 보쌈, 족발 등 육류와의 궁합이 좋다.',3,3,4,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(382,'인천 생 소성주',6,1200,'쌀, 팽화미, 정제수, 국, 효모, 아스파탐, 효소, 이소말토 올리고당','인천탁주','인천광역시 부평구 안남로 433번길 14','인천광역시 부평구','TAK','원료인 팽화미에서 나오는 고소한 향이 올라오는 것이 특징이다. 전체적으로 바디감과 목넘김이 청량하고 가벼우며 삼킨 후 남는 은은한 텁텁함이 좋다. 생선회와 잘 어울린다.',1,2,1,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(383,'입장탁주',6,1200,'쌀, 정제수, 고과당, 효모 등','농업회사법인 입장주조㈜','충청남도 천안시 서북구 입장면 성진로 808','충청남도 천안시','TAK','술 빛은 아이보리 색과 같고 팽화미를 이용해 고소한 향과 발효향이 동시에 올라온다. 입에 넣으면 처음에는 신맛과 단맛이 동시에 올라오고 술을 마시고 난 후에 고소함이 혀에 남아 뒷맛을 이끌어 간다. 버섯 전골류와 잘 어울린다.',2,2,2,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(384,'자두와인 레드',12,22000,'피자두','자두사랑','경상북도 김천시 대학로 214','경상북도 김천시','WIN','진한 진주빛에 붉은 과실에서 나는 과실향과 훈연된 향이 올라온다. 신맛과 단맛이 잘 갖춰져 있으며 전체적인 술맛은 산미가 대장 노릇을 한다. 술을 오픈하고 어느 정도 산소 접촉을 시키면 더욱 부드럽게 마실 수 있다. 산미가 있어 입에 침이 고이고 아주 깔끔하게 마실 수 있는 술이다. 피자 등과 잘 어울린다.',2,3,2,2,0,0,0);
