insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(386,'자연담은 복분자막걸리',6,56000,'복분자, 쌀, 효모, 국, 과당, 구연산, 젖산, 정제수','(농)국순당고창명주(주)','전라북도 고창군 심원면 선운대로 2197','전라북도 고창군','TAK','복분자와 쌀이 혼합된 막걸리로 신선한 과실향에 부드럽게 올라오는 곡물향이 잘 섞여 있다. 단맛과 신맛 등 오미의 조화가 잘 갖춰져 있고, 막걸리를 살균하여 청량미는 적지만 맑은 산미가 입 전체를 깔끔하게 만든다. 입에 넣는 순간 ‘맛있다’라고 느껴진다. 복분자 막걸리에는 장어구이가 기본이다.',2,1,1,1,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(387,'자연송이주',18,0,'자연송이버섯','솔래원','강원도 양구군 방산면 칠전길 12-7','강원도 양구군','SPI','밝은 미색을 가지고 있으며 은은하게 단맛과 섞인 버섯향이 올라온다. 알코올도수가 낮아 입 안에 편안하게 들어오고 아주 엷은 버섯의 맛과 향이 혀를 적신다. 전체적으로 가볍고 편하게 마실 수 있는 술이다. 동해바다에서 나오는 신선한 활어회와 잘 어울린다.',1,0,0,1,0,1,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(388,'자희향',12,16000,'찹쌀(국내산), 멥쌀(국내산), 누룩(국내산), 정제수','자희자양','전라남도 함평군 신광면 신해로 92','전라남도 함평군','TAK','단맛과 신맛 등 오미의 균형이 잘 갖춰져 있으며 술 속 미세한 입자들이 입 안 전체를 부드럽게 만든다. 연한 쓴맛과 텁텁함이 술에 힘을 실어준다. 해물찜과 잘 어울린다.',2,2,1,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(389,'자희향 국화주',15,20000,'찹쌀, 멥쌀, 누룩, 정제수, 국화','자희자양','전라남도 함평군 신광면 신해로 92','전라남도 함평군','CHE','달콤한 향이 묵직하게 올라온다. 첫맛은 달게 느껴지지만 점차 오미를 모두 느낄 수 있는 것이 특징이다. 두부김치와 잘 어울린다.',1,2,1,4,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(390,'자희향 나비',8,8000,'찹쌀(국내산), 멥쌀(국내산), 누룩(국내산), 정제수','자희자양','전라남도 함평군 신광면 신해로 92','전라남도 함평군','TAK','은은한 수박향이 올라오고 첫 맛과 목넘김 후 뒷맛이 가볍다. 입 안에서 부드러운 아이스크림이 녹듯 사라진다. 혀에 남는 미세한 텁텁함이 좋다. 피자랑 잘 어울린다.',2,1,2,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(391,'장수 생 막걸리',6,1200,'백미(국내산), 말토올리고당','서울장수주식회사','충청북도 진천군 광혜원면 죽현길141','충청북도 진천군','TAK','일반적인 서울장수막걸리에서 느껴지는 익숙한 단맛과 과일향과 함께 상쾌한 탄산기운을 느낄 수 있다. 전체적으로 순하고 부드러운 맛이다. 목으로 넘길 때 탄산의 느낌이 강하여 시원하고 긴 여운이 남는다. 막걸리 안주로 생각할 수 있는 음식에는 어느 것이나 궁합이 맞다.',2,3,3,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(392,'장수 오미자주',16.5,5400,'오미자(국내산), 정제수, 주정, 설탕, 효모','농업회사법인 (주)알에프','전라북도 장수군 계남면 침곡로 11-26','전라북도 장수군','WIN','맑은 오미자 차와 같은 아름다운 색을 가지고 있다. 향에서는 오미자 특유의 새콤함과 매콤함이 그대로 느껴지며, 맛에서는 오미자 특유의 단맛, 짠맛, 신맛, 쓴맛, 매운맛이 느껴진다. 각각 톡톡 튀는 맛을 자랑하는 듯 하지만, 각자의 맛이 조합이 잘 된 밸런스를 자랑하며, 덕분에 부드러운 후미를 가지고 있다. 산악지대에서 자라는 오미자인 만큼 산나물, 들기름으로 볶아 낸 버섯요리 등과 잘 어울릴 듯하다. 기회가 된다면 송이버섯 전골과 함께 즐긴다면 더할 나위 없이 매력적일 듯하다.',3,4,4,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(393,'장홍삼 장수 막걸리',6,2000,'백미, 말토올리고당, 홍삼분말','서울장수주식회사','충청북도 진천군 광혜원면 죽현길141','충청북도 진천군','TAK','인삼향과 곡물향이 잘 섞여 코 안으로 들어온다. 입에 넣으면 산미가 가장 먼저 올라오고 그 다음 탄산이 입 전체를 시원하게 만들어 준다. 산미와 청량감이 술 전체를 가볍게 만들어주고 목넘김을 매우 깔끔하게 해준다. 삼겹살 등과 잘 어울린다.',1,4,3,1,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(394,'정고집 나주배 약주 15도',15,20000,'쌀, 쌀가루(국내산), 배착즙액(나주산), 정제효소제, 종국, 젖산, 효모','남도탁주','전라남도 나주시 노안면 노안로379','전라남도 나주시','CHE','당도 높은 배 맛이 그대로 생각나는 맛. 과육 없는 배과즙을 먹는 듯 한 느낌도 있지만, 끝에서 느껴지는 약주 특유의 쌀 향도 느껴진다. 담백한 맛의 한우 수육과 즐긴다면, 소고기와 배과즙라는 멋진 궁합을 느낄 수 있다.',4,2,3,3,0,0,0);
