insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(396,'정고집 나주쌀 생막걸리',6,12000,'쌀, 쌀가루(국내산), 조제종국, 정제효소, 젖산, 아스파탐, 아세셀팜칼','남도탁주','전라남도 나주시 노안면 노안로379','전라남도 나주시','TAK','가벼운 누룩 향이 살짝 코끝을 스치는 듯 한 향. 쌀이 주는 맑은 아이보리 컬러에 마지막 후미까지 은은한 쌀의 향을 느낄 수 있다. 나주산 막걸리인 만큼 영산포의 홍어삼합이 생각나는 맛이다.',3,3,3,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(397,'정고집 옛날 생동동주',6,18000,'쌀, 쌀가루(국내산), 조제종국, 정제효소, 젖산, 아스파탐, 아세셀팜칼','남도탁주','전라남도 나주시 노안면 노안로379','전라남도 나주시','TAK','동동주답게 쌀이 동동 떠 있는 모습이 독특함. 단맛과 산미, 그리고 쌀의 담백함이 주는 밸런스가 좋은 막걸리이다. 두부김치, 부침개, 감자전 등과 곁들이면 청량한 맛을 자아내는 동동주의 풍미를 더욱 잘 느낄 수 있다.',3,3,4,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(398,'제주 고소리술',40,45000,'잡곡, 누룩, 정제수','제주술익는집','제주특별자치도 서귀포시 표선면 중산간동로 4726','제주특별자치도 서귀포시','SPI','우리나라의 전통증류기인 토기로 만든 고소리(소줏고리)로 직접 내려 매우 귀한 소주이다. 상압증류(일반 기압에서 증류)로 증류해 맛과 향의 풍미가 강하고 좁쌀과 보리가 들어가 독특한 크림 같은 맛을 지니고 있으며, 기본 3년 숙성된 술이라 매우 부드러운 목 넘김을 가지고 있다. 기름기 많은 고등어 회와 잘 어울린다.',0,0,0,3,0,4,1);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(399,'제주샘주 오메기술',13,7800,'쌀, 차조, 입국, 누룩, 효모, 효소, 청호, 조릿대, 감초, 정제수','제주샘영농조합법인','제주특별자치도 제주시 애월읍 애원로 283','제주특별자치도 제주시','CHE','묵직한 찻잎의 향기와 시트러스한 향미가 동시에 느껴진다. 조화로운 감미와 산미, 목 뒤로 느껴지는 감칠맛이 좋다. 농축미가 느껴지는 약주이다. 방어 등 붉은 생선 회, 해산물 등의 음식과 함께하면 좋다.',3,2,1,2,4,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(400,'젤코바 스페셜와인',11.5,17000,'국내산 산머루, MBA포도','농업회사법인 (주)젤코바와이너리','경상북도 상주시 청리면 남상주로 1108-3','경상북도 상주시','WIN','산머루 특유의 진한 자줏빛의 색을 가진 와인이다. 향에서는 숙성을 통해 만들어진 산미가 지긋이 올라오며, 마무리는 상큼한 단향이 올라온다. 입안에 머금어도 산미부터 올라오며 이내 단맛이 입안을 메워준다. 머루 특유의 드라이함이 남아있으며, 덕분에 유럽 와인에서 보는 타닌감도 살아있다. 식중주로 충분히 즐길 수 있으며, 진한 맛이 좋은 만큼 다소 양념이 된 고기류와도 좋다. 불고기, 양념갈비 등을 추천해 본다.',2,4,2,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(401,'젤코바 캠벨와인',13.5,62500,'유기농 캠벨포도, 유기농 설탕','농업회사법인 (주)젤코바와이너리','경상북도 상주시 청리면 남상주로 1108-3','경상북도 상주시','WIN','캠벨얼리 특유의 맑은 붉은빛을 가진 레드와인이다. 일상에서 자주 접하는 포도향이 물씬 나는 와인이다. 그렇다고 해서 맛도 마냥 단맛으로 일괄되지는 않는다. 적절한 산미와 작게나마 타닌감이 느껴진다. 오히려 단맛은 적은 편으로 다양한 음식과 매칭이 가능하다. 와인 특유의 맛을 즐기고 싶다면 제철별로 나오는 산나물이 좋으며, 한식을 베이스로 한 육류요리와도 좋다. 흰 살 생선 및 붉은 살 생선에도 적절히 잘 어울린다.',3,4,2,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(402,'젤코바 프리미엄와인',12,33000,'국내산 산머루, MBA포도','농업회사법인 (주)젤코바와이너리','경상북도 상주시 청리면 남상주로 1108-3','경상북도 상주시','WIN','산머루 특유의 진한 루비색을 가진 레드 와인. 포도의 향기는 물론 포도가지, 껍질까지 포함한 진하고 깊은 향을 가지고 있다. 첫맛은 상큼한 산미가 입안을 치며, 다소 단맛이 스치듯 지나가지만 이내 레드와인 특유의 타닌감으로 입속을 마무리한다. 단맛이 적고 떫은맛이 좋아 식중주로 추천하고 싶으며 육류와 절대적으로 잘 어울리는 와인이다. 특히 생고기류 하고도 잘 어울리겠지만, 양념 소갈비, 육전 등 맛이 가미된 고기류를 추천해 본다.',1,4,2,4,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(403,'조선주조사',14,4000,'쌀(국내산), 입국, 누룩, 주정, 정제수','농업회사법인 우포의아침(주)','(1공장) 경상남도 창녕군 대지면 대지농공단지길 40, (2공장) 경상남도 창원시 의창군 북면 천주로 1173번길29','(1공장) 경상남도','CHE','약주 특유의 담황색을 가지고 있지만 한약 향 등은 전혀 느껴지지 않는 맑은술이다. 옥수수에서 나오는 듯 한 고소함이 있으며, 입속에 머금는 순간 적절한 부드러움이 느껴진다. 단맛, 신맛 등의 조화가 잘 이뤄져 있어서 부담되지 않는 맛과 향을 가지고 있다. 집안에서 홈술로 즐기기 좋은 술이며, 김치찌개, 된장찌개 등 일상의 음식과 즐기기 좋다.',3,3,2,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(404,'주몽 복분자주',16,5000,'쌀, 복분자','농업회사법인(유)술소리','전라북도 남원시 시묘길 130(오암동)','전라북도 남원시','ETC','붉은 진주빛을 가지고 있으며 잘 익은 검은 과실향이 올라온다. 술을 입에 넣으면 단맛이 가장 먼저 올라오고 그 다음 산미가 뒤따라온다. 목넘김은 가볍고 깔끔하다. 술을 마시고 난 후에 미세하게 남는 쓴맛과 알코올 맛을 느낄 수 있다. 케이크 등 디저트 음식과 잘 어울린다.',3,1,1,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(405,'주산지 막걸리',6,1200,'쌀(국내산), 밀가루, 누룩, 입국, 효모, 고과당, 물엿, 아스파탐, 삭카린나트륨, 정제수','청송양조장','경상북도 청송군 부남면 대전로 121-1','경상북도 청송군','TAK','진한 아이보리 빛깔에 고소한 향이 특징이다. 술 맛은 전체적으로 단맛과 신맛의 조화가 잘 갖춰져 있고 입 안의 느낌이 개운하다. 술을 마시고 난 후 약간의 텁텁함이 남는데 이것이 음식을 당기게 한다. 양념된 고기류와 잘 어울린다.',2,1,2,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(406,'죽력고',32,60000,'국내산쌀, 죽력, 누룩, 정제수','태인합동주조','전라북도 정읍시 태인면 태흥리 392-1','전라북도 정읍시','SPI','죽력고는 연록색을 띠며 알싸하고 상쾌한 맛이 나며 대나무 특유의 향이 난다. 살짝 한약냄새가 나면서 알코올 두수 35%의 고도주답게 강한 알코올 향이 느껴진다. 첫맛은 청량감 있게 다가오지만 살짝 단맛이 느껴지고 진한 알코올 기운이 혀를 자극한다. 죽력고의 안주로는 담백한 음식도 좋지만 단맛이 살짝 도는 떡갈비와 같은 안주도 잘 어울린다.',0,0,0,3,0,4,2);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(407,'죽향 생막걸리',6,2000,'쌀(국내산), 정제효소, 젖산, 효모, 아스파탐, 정제수','농업회사법인 (주)죽향도가','전라남도 담양군 담양읍 외다길30','전라남도 담양군','TAK','연한 아이보리 빛깔에 곡물향이 은은하게 스쳐 지나간다. 단맛이 적고 산뜻한 신맛이 매력적인 술이다. 술을 마시고 난 후에도 가벼운 산미와 시원함이 더운날 시원한 바람에 흔들리는 대나무와 같다. 죽순무침과 함께 마시면 더할 나위 없다.',1,2,4,1,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(408,'줄포 생 막걸리',6,1200,'쌀(국내산), 종국정제수, 누룩, 효모, 아스파탐','내변산(동진주조)','전라북도 부안군 행안면 부안농공단지길 14-11','전라북도 부안군','TAK','참외나 메론의 시원한 단맛이 인상적이다. 뒷 여운의 쓴맛이 적으로 어디에서 구입하던 맛의 편차가 크지 않은 생막걸리이다. 처음과 끝까지 목넘김이 부드럽고 여름과일의 달큼한 향이 감돈다. 각종 전 안주와 어울린다.',2,2,3,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(409,'지란지교 무화과 탁주',12,30000,'찹쌀, 멥쌀(국내산), 전통누룩(국내산 밀), 정제수, 무화과청, 레드비트분말, 밀함유','(유)친구들의술지란지교','전라북도 순창군 순창읍 순창7길 5-1','전라북도 순창군','TAK','생과일 딸기주스 같은 앵둣빛을 띤다. 진한 분홍색으로 고구마색 같기도 하다. 레드비트(무)향과 무화과향의 밸런스가 좋다. 의외의 조합이었지만 무화과와 레드비트의 캐릭터가 탁주 본연의 맛과 잘 어우러진다. 목 넘김은 약간 묵직한 편이다. 함께 먹기에 어울리는 음식으로 갈치조림, 고등어무조림, 무화과타르트가 있다.',4,4,1,4,4,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(410,'지란지교 약주',17,20000,'찹쌀 · 멥쌀(국내산), 전통누룩(국내산 밀), 정제수, 밀함유','(유)친구들의술지란지교','전라북도 순창군 순창읍 순창7길 5-1','전라북도 순창군','CHE','레몬빛의 연노란색을 띤다. 은은한 녹색빛이 옅게 묻어난다. 조청향과 곡물향, 식혜에서 느껴질 법한 엿기름향이 주를 이룬다. 농익은 단맛과 산미에서 감칠맛이 느껴진다. 조화가 좋다. 알코올 도수에 비해 목 넘김이 부드럽다. 함께 먹기에 어울리는 음식으로 오징어초무침, 고등어김치찜, 연어포케가 있다.',4,4,4,4,4,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(411,'지란지교 탁주',13,30000,'탁주 찹쌀(국내산), 멥쌀(국내산), 누룩(국내산), 정제수','(유)친구들의술지란지교','전라북도 순창군 순창읍 순창7길 5-1','전라북도 순창군','TAK','전체적으로 술 지게미로 인해 무게감이 있고 입안을 꽉 채우고 난 후 목으로 넘어가는 것도 무겁게 느껴진다. 하지만 단맛과 신맛이 있어 부담감은 적다. 향에서는 다양한 향이 있고 맛에서는 단맛과 신맛이 강한 편이나 전체적인 균형감이 좋다. 무게감이 있어서 첫 입에서의 느낌은 강하지만 전체적으로 마시기에는 불편함이 없다. 전, 육전, 떡볶이와 잘 어울린다.',4,4,1,4,4,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(412,'지리산 허브잎 술',6,1500,'쌀(국내산), 종국, 정제수, 누룩, 효모, 국내산허브','지리산그린영농조합 운봉주조','전라북도 남원시 운봉읍 황산로 1018','전라북도 남원시','TAK','연한 노란빛이 인상적이다. 익은 곡물향이 강하게 올라오고 그 사이로 달콤함 향이 묻어 나온다. 단맛은 적고 연한 산미가 좋아 입 안 전체를 가볍게 만들어 준다. 전체적으로 단맛이 적어 음식과의 궁합을 잘 맞출 수 있는 술이다. 특히 닭갈비랑 잘 어울린다.',1,2,2,1,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(413,'지리산정담생쌀 막걸리',6,1500,'쌀(국내산), 종국, 정제수, 누룩, 효모','지리산그린영농조합 운봉주조','전라북도 남원시 운봉읍 황산로 1018','전라북도 남원시','TAK','색의 희고 맑으며 곡물에 압력을 가했을 때 나오는 고소한 향이 올라온다. 단맛이 적고 맑은 산미가 입안 전체에 침이 고이게 하고 산뜻하게 만든다. 막걸리를 마시고 난 뒤에 혀에 남는 고소함이 매력적이다. 신선한 과일 샐러드와 잘 어울린다.',1,3,2,1,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(414,'지장수 생막걸리',6,1300,'정제수(지장수), 쌀, 효모, 누룩, 과당 등','낙천','강원도 동해시 공단2로 73','강원도 동해시','TAK','단맛과 신맛의 균형이 잘 이루어진 일반적인 막걸리 맛이지만 입 안에 남는 연한 쓴맛이 음식과의 조화를 잘 맞출 수 있는 술이다. 추천 안주는 굴 튀김',3,2,3,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(415,'지장수 호박 막걸리',6,5000,'정제수(지장수), 쌀, 효모, 누룩, 과당 등','낙천','강원도 동해시 공단2로 73','강원도 동해시','TAK','연한 노란 빛깔 감돌고 단맛과 신맛의 조화가 잘 갖춰져 있다. 입 안에 감도는 떫음과 쓴맛이 특징이다. 겉절이 김치와 수육이 잘 어울린다.',2,1,1,4,0,0,0);
