insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(420,'진맥소주 22도',22,22000,'유기농 우리밀 100%','농업회사법인 (주)밀과노닐다','경상북도 안동시 도산면 신성중앙길32','경상북도 안동시','SPI','증류주에서도 감칠맛이 날 수 있다는 것을 보여줄 수 있는 거의 유일한 술일듯하다. 증류한 술에 물을 희석하면 밍밍한 맛이 많이나게 되는데 이 술은 물과 증류원액의 밸런스가 잘 잡혀있다. 이것은 오랜 숙성이 되어야만 가능한 맛이다. 너무 차게 마시게 되면 진맥소주의 가장 큰 장점이라고 할 수 있는 감칠맛을 못 느낄 수 있으니 20도 정도에서 마시길 권장한다. 돼지고기 수육과 환상의 궁합이다.',0,0,0,2,0,3,2);
