insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(459,'충주 백주',35,7800,'쌀증류원액(국내산), 당귀, 아스파탐, 아세설팜K, 구연산, 정제수','고헌정영농조합법인','충청북도 충주시 산척면 샛강영길 39','충청북도 충주시','SPI','무색투명한 색을 가진 소주로 가벼운 쌀 향부터 시작한다. 35%의 높은 알콜올 도수에도 불구하고 단맛이 초반부터 올라오며, 이내 가벼운 신맛으로 마무리된다. 단맛이 강조된 술인 만큼 듬뿍 양념이 된 음식과 매칭이 좋다. 또 알콜올 도수가 높은 만큼 해장을 생각한 국물요리가 잘 어울린다. 매운 해물탕, 감자탕부터 돼지고기 김치찌개 등의 대중적인 음식도 좋은 매칭이다. 알코올 도수가 높아 바로 마시기 어렵다면 얼음이나 시원한 탄산수를 넣어 희석해서 마셔도 좋다.',0,0,0,3,0,4,2);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(460,'컨츄리 산머루 와인 (드라이)',14,20000,'국산 산머루원액 100%','컨츄리 농원','충청북도 영동군 영동읍 주곡리 590-14','충청북도 영동군','WIN','붉은 흑진주 색에 농익은 검붉은 과실향이 함께 올라오고 단맛은 적지만 산미가 있어 입 안이 매우 산뜻하고 아주 옅은 타닌이 입 안을 자극시킨다. 여운이 길다. 오리구이와 잘 어울린다.',1,3,3,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(461,'컨츄리 산머루 와인 (스위트)',12,20000,'국산 산머루원액 100%','컨츄리 농원','충청북도 영동군 영동읍 주곡리 590-14','충청북도 영동군','WIN','진한 검붉은 빛의 색에 훈연된 향과 가죽향, 잘 익은 베리향 등이 풍만하게 올라온다. 단맛과 신맛의 균형이 갖춰져 있으며 바디감이 좋고 입 안에 남는 옅은 쓴맛이 좋다. 삼겹살과 잘 맞는다.',2,3,2,2,0,0,0);
