insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(463,'코이버펑크, 머스캣 베일리 에이',12,65000,'와이너리 재배 포도 100%','코이버펑크','경상북도 영천시 봉계길 52','경상북도 영천시','WIN','머스캣 베일리 에이(Muscat Baily A)는 다른 머스캣 베일리 에이 와인과 달리 더 좋은 복합성을 지니고 있다. 라즈베리, 딸기, 블랙베리 등 다양한 베리류 과실과 토스트, 후추, 스파이스 등 가벼운 오크향이 이상적으로 어우러져 입안에 퍼진다. 전체적으로 무겁지 않은 타닌감이 구조를 잘 이룬다. 약간의 단맛의 여운이 길게 이어지는 특징으로 고추장, 된장 등 한국 장 요리와 잘 어울리는 와인이다.',3,3,3,4,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(464,'코이버펑크, 샤르도네 세미용 블렌드',11,65000,'와이너리 재배 포도 100%','코이버펑크','경상북도 영천시 봉계길 52','경상북도 영천시','WIN','샤르도네(Chardonnay) 세미용(Semillon) 블렌드 화이트 와인은 와인 양조 시 사람의 개입을 최소화해 보다 자연적인 방식으로 양조했다. 기존의 잘 알려진 샤르도네 와인과 특징이 매우 다르다. 레몬, 귤 등 시트러스 과실을 연상시키는 상큼함이 섬세한 미네랄 특징과 잘 어우러진다. 흙내음, 버섯 같은 특유의 자연미가 약간의 당도와 잘 어우러져 한식과 부담 없이 즐길 수 있는 와인이다.',2,4,4,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(465,'코이버펑크, 카베르네 소비뇽',12,65000,'와이너리 재배 포도 100%','코이버펑크','경상북도 영천시 봉계길 52','경상북도 영천시','WIN','카베르네 소비뇽(Cabernet Sauvignon)은 와인 양조 시 사람의 개입을 최소화해 자연적인 방식으로 양조했다. 기존의 잘 알려진 카베르네 소비뇽 품종 특징과 매우 다르다. 보다 섬세하고 여리여리한 특징을 가졌다. 잘 익은 딸기, 라즈베리, 레드 커런트 등 붉은 과실이 특징이 뚜렷하고 토스트, 코코넛 새 오크의 특징이 잘 입혀져 복합성을 더한다.',1,2,2,3,0,0,0);
