insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(471,'톡 쏘는 복분자 막걸리',6,1800,'쌀, 정제수, 쌀입국, 복분자원액, 효모 등','가평 우리술','경기도 가평군 조종면 대보간선로 26, 29','경기도 가평군','TAK','쌀막걸리의 가벼움에 복분자 원액이 혼합된 막걸리다. 과실 막걸리에 맞게 단맛이 매우 강하고 산미가 적으며 청량감이 강하다. 생일 등 축제 때 디저트 술로 알맞다.',5,1,3,1,0,0,0);
