insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(474,'톡 한잔 소주',30,10000,'찰보리 90%, 쌀 10%','농업회사법인(유)대마주조','전라남도 영광군 대마면 동삼로 699','전라남도 영광군','SPI','높은 불에 증류한 듯한 불향과 고소함이 함께 올라온다. 입에 넣으면 알코올 도수에 비해 도수가 낮아 보여 술이 부드럽게 넘어간다. 술을 마시고 난 후에 박하와 같이 화한 느낌이 혀에 남아 깔끔함을 준다. 황태전골, 두부전골 등 국물요리와 잘 어울린다.',2,0,0,3,0,2,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(475,'티나',16,54000,'딸기추출물, 죽력, 정제수, 주정, 액상과당(포도당), 구연산, 딸기향, 망고향','추성고을','전라남도 담양군 용면 추령로 29','전라남도 담양군','SPI','달콤한 딸기향에 예쁜 핑크색을 가진 술이다. 마치 맛있는 딸기사탕을 먹는 듯 하다. 알코올 도수가 낮아 누구나 편하게 즐길 수 있고, 색이 예쁘고 밤에 조명도 켜지기 때문에 로맨틱한 분위기를 연출한다. 여성들이 분위기 있는 곳에서 달콤하게 즐길 수 있는 술이다. 양념된 안주류보다는 캐러멜이나 케익 등과 함께하기 좋은 술이다.',0,0,0,5,0,4,4);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(476,'펀치 쌀 바나나',4,2000,'쌀(국내산), 바나나농축액, 효모, 종곡, 정제수, 합성착향료, 합성감미료','내변산(동진주조)','전라북도 부안군 행안면 부안농공단지길 14-11','전라북도 부안군','ETC','병 뚜껑을 오픈하자마자 바나나 향을 느낄 수 있다. 술 빛은 아주 맑은 아이보리 색깔을 가지고 있으며 입에 넣으면 달콤함과 청량감이 동시에 입 전체에 퍼진다. 첫 맛은 달지만 혀에 느껴지는 산미가 입 안을 가볍게 만들어 준다. 전체적으로 맛있게 마실 수 있는 술이다. 달콤한 디저트 음식과 잘 어울린다.',3,1,2,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(477,'편백숲 산소막걸리',5.2,1900,'멥쌀, 올리고당, 사과농축액, 누룩, 아스파탐','농업회사법인 (주)청산녹수','전라남도 장성군 장성읍 남양촌길(백계리) 19','전라남도 장성군','TAK','연한 아이보리 빛깔을 갖고 있으며 은은한 발효향이 잘 올라온다. 단맛과 신맛의 조화가 잘 갖춰져 있으며 입 안에서의 느낌이 탄산에 의해 매우 경쾌하고 언제든 편하게 마실 수 있는 술이다. 편한 모임자리에서 거부감 없이 편하게 즐길 수 있는 술이다. 청량감과 산미의 조화가 기름진 전이나 고기류와 잘 어울린다.',3,3,3,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(478,'편백숲 산소막걸리 딸기스파클링',6.8,41000,'멥쌀, 찹쌀, 누룩, 딸기, 올리고당','농업회사법인 (주)청산녹수','전라남도 장성군 장성읍 남양촌길(백계리) 19','전라남도 장성군','TAK','짙은 자색빛깔을 가진 딸기 스파클링 막걸리로 탄산이 매우 강한 것이 특징이다. 병을 오픈할 때 서서히 탄산이 올라오는 것을 즐길 수 있다. 술을 입에 넣는 순간 달콤한 딸기맛이 입 전체를 감싸준다. 신선한 청량감이 술을 마시고난 후에도 깔끔하게 만들어 준다. 수육 등 약간의 기름진 음식들과 잘 어울린다.',3,2,5,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(479,'편백숲 산소막걸리 순수령',5.8,4000,'멥쌀, 찹쌀, 누룩','농업회사법인 (주)청산녹수','전라남도 장성군 장성읍 남양촌길(백계리) 19','전라남도 장성군','TAK','흰색의 막걸리로 곡물과 효모의 발효향이 은은하게 올라온다. 곡물에서 나온 부드러운 단맛이 입 전체를 꽉 채워준다. 적절한 산미가 입 안을 개운하게 해주고 미세한 곡물가루가 혀에 부드러움을 안겨줘 독특한 느낌을 준다. 술을 잘 마시지 못하는 분들도 맛있게 마실 수 있는 막걸리다. 식사 후 호두나 땅콩 등과 함께하기 좋은 술이다.',3,1,1,4,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(480,'평창 오디주',16,19500,'국내산 오디','농업회사법인(주)홍지원','강원도 평창군 평창읍 농공단지길 24-14','강원도 평창군','WIN','검은 담황색의 술빛을 가지고 있으며 푹익은 과실향이 올라온다. 단맛은 적고 산미가 술 전체의 맛을 이끌어 간다. 첫 맛은 묵직하게 들어오지만 금새 사라지고 가볍게 느껴지는 맛이다. 술을 마시고 난 후에는 술을 언제 먹었냐는 듯 입 안에 깔끔하다. 감자전과 잘 어울린다.',1,3,1,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(481,'평창머루주',16,19500,'국내산 산머루','농업회사법인(주)홍지원','강원도 평창군 평창읍 농공단지길 24-14','강원도 평창군','WIN','흑진주의 색을 가지고 있으며 익은 과실 껍질향이 올라온다. 전체적으로 산미가 자리를 잡고 그 위에 약한 단맛과 쓴맛이 자리를 잡는 형국이다. 적절한 산미가 입 안 전체를 가볍게 만들어주지만 알코올에 의해 목넘김이 강하게 느껴진다. 빨리 안주를 먹고 싶다. 삼합이 잘 어울린다.',1,3,1,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(482,'포엠 레드 드라이와인',12,25000,'포도(국내산_MBA), 머루(국내산)','갈기산포도농원 (주)농업회사법인','충청북도 영동군 학산면 모리1길 23','충청북도 영동군','WIN','은은한 루비 색을 가진 와인. 산미와 진한 산머루 향을 담은 기분 좋은 흙향이 올라온다. 입안에 머금으면 미세한 단맛이 입속을 적셔주면서 묵직한 타닌감이 입안을 감싸주며 상큼한 신맛이 식욕을 돋워주며 마무리된다. 식전 주와 디저트 용보다는 식중주로 식사와 함께 즐길 수 있다. 기존의 레드와인이 가진 육류와 잘 어울리는 정체성을 가지고 있어서 스테이크, 한우 등심구이, 생선요리로는 메로구이, 삼치구이와도 잘 어울리는 맛이다.',2,3,2,4,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(483,'포엠 레드 스위트와인',12,25000,'포도(국내산_캠벨얼리, MBA)','갈기산포도농원 (주)농업회사법인','충청북도 영동군 학산면 모리1길 23','충청북도 영동군','WIN','맑은 루비색을 가진 와인이다. 캠벨 포도 특유의 상큼함이 그대로 느껴지는 향을 가지고 있다. 마시는 순간 새콤한 산미가 먼저 시작을 하며, 마지막에 달콤한 맛이 느껴진다. 스위트 계열이긴 하지만, 마냥 달지만은 않고, 가벼운 타닌감의 터치도 느껴진다. 와인을 어려워하는 초심자에게 잘 어울리는 제품으로 기존의 레드와인보다는 다소 칠링을 해서 즐기면 더욱 맛있게 마실 수 있는 맛이다.',4,3,3,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(484,'포엠 로제와인',12,35000,'포도(킹데라웨어)','갈기산포도농원 (주)농업회사법인','충청북도 영동군 학산면 모리1길 23','충청북도 영동군','WIN','레드 다이아몬드의 색을 가진 와인. 향이 바로 치고 오지는 않지만, 잔잔히 올라오는 포도향이 좋다. 살짝 입을 대면 향에서 숨겨졌던 잘 익은 포도 맛이 그대로 올라온다. 떫은맛, 타닌감, 산미는 지극히 적지만, 이 맛 하나로도 충분히 맛을 표현하고 있다. 단맛이 많은 만큼 식중주보다는 식전 주나 식후 주를 추천하고 싶으며, 드레싱 없이 생야채만 함께 즐긴다면 이 와인의 특징을 느낄 수 있을 것이다.',4,3,3,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(485,'포엠 화이트와인',12,35000,'청포도(청수)','갈기산포도농원 (주)농업회사법인','충청북도 영동군 학산면 모리1길 23','충청북도 영동군','WIN','옐로 다이아몬드의 색을 그대로 가진 화이트 와인이다. 달콤함과 산뜻함을 가진 향, 더불어 잘 익은 청포도의 향이 뒷받쳐 준다. 입속에 머금은 순간 잘 익은 포도를 깨무는 듯한 식감이 펼쳐지며 마지막에 떨어지는 신맛은 식욕을 더욱 돋우는 매력이 있다. 달콤함은 식전주, 식주에서 활약할 수 있지만, 산미 역시 좋기에 식중주로도 가능하다. 이왕이면 와인 맛을 잘 느끼고자 담백한 요리를 추천한다. 주로 바닷가 요리와 잘 어울릴 수 있으며, 담백한 광어, 우럭 및 조개찜 등과도 좋은 조합을 보일 것이다.',4,4,4,3,0,0,0);
