insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(510,'해창 생 막걸리 12도',12,10000,'햅쌀, 찹쌀, 물, 누룩','해창주조장','전라남도 해남군 화산면 해창길1','전라남도 해남군','TAK','찹쌀의 감칠맛과 멥쌀의 센맛이 잘 어울어진 막걸리로 인위적이지 않은 원재료의 단맛이 있지만 강하지 않고 산미가 적당해 어떤 음식과도 잘 어울릴 수 있는 막걸리다. 6도나 9도보다 산미가 적고 청량미가 강하지 않다. 꼬막 무침 등과 잘 어울린다.',1,1,2,4,0,0,0);
