insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(513,'해파랑',13,10000,'쌀(국내산), 해방풍(국내산), 계량누룩, 젖산, 이소말토올리고당, 정제수','농업회사법인(주)영덕주조','경상북도 영덕군 강구면 소월1길 16-10','경상북도 영덕군','CHE','잘 익은 황금색 벼를 닮은 색을 가지고 있으며 잘 익은 곡물 향미가 입 전체를 자극시킨다. 발효 과정 중에 방풍이 들어가 독특한 향과 맛을 가지고 있다. 전체적으로 단맛과 고소한 맛이 주를 이루고 산미가 적어 삼계탕과 함께 먹으면 좋다.',3,1,1,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(514,'햇찹쌀이 하늘수',6,1100,'쌀(국내산), 찹쌀(국내산), 전통누룩, 정제수','안양주조장','전라남도 장흥군 안양면 주교비동로4','전라남도 장흥군','TAK','옅은 아이보리 컬러를 가진 막걸리다. 하늘거리는 누룩향과 살포시 느껴지는 사과향이 있다. 자연 탄산이 주는 청량감이 매력 있으며, 단맛과 신맛, 그리고 목넘김을 하나하나 다 맛볼 수 있다. 500ml의 작은 용량으로 핸드캐리 하기에 좋은 막걸리이다. 운동 후, 등산 후에 마시면 좋은 스타일이다. 청량감이 좋은 만큼 매콤한 요리도 잘 어울리는데, 기회가 된다면 꼬막무침, 닭볶음탕 등 다소 매운 요리가 잘 어울릴 것이다. 또한 장흥의 특산물인 키조개무침, 키조개전/ 장흥 한우 삼합구이 등과 같이 마시는 것도 권해 드린다.',3,3,3,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(515,'행운 막걸리',6,1100,'쌀(국내산), 소맥분(미국산), 전분당, 정제수, 정제효소, 종국, 효모, 젖산, 아스파탐','성포양조장','경상남도 거제시 사등면 지석3길3','경상남도 거제시','TAK','진한 아이보리 빛깔에 술에 빵굽는 향이 매력적이다. 입 안에 술을 넣는 순간 매우 고소한 빵을 먹는 느낌이다. 전체적으로 산미가 적지만 단맛 또한 강하지 않아 입 안의 느낌이 무겁지 않는 것이 좋다. 마치 든든한 한끼의 식사를 하는 것 같다. 돼지김치볶음이나 불고기와 잘 어울리다.',3,1,1,4,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(516,'행운주',12,3500,'백미(국내산), 밀누룩(국내산), 정제수, 종국, 효모, 아스파탐','성포양조장','경상남도 거제시 사등면 지석3길3','경상남도 거제시','CHE','엷은 노란색을 가진 술로 잘 익은 과일의 단 향기가 매력적인 술이다. 전체적으로 단맛과 신맛의 조화가 잘 갖춰져 있으며, 뒤에 쌉싸름한(조금 쓴맛이 있는) 느낌이 술을 마시고 난 후에 긴 여운을 남기게 한다. 일반 약주에 비해 도수(11%)가 낮아 저녁 식사자리에서 약간 매운 음식과 함께 하기 좋은 술이다.',2,2,2,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(517,'향수 우리밀 막걸리',9,7900,'우리밀, 누룩, 정제수, 무감미료','이원양조장','충청북도 옥천군 이원면 묘목로 113','충청북도 옥천군','TAK','100% 우리밀, 무감미료로 빚어진 만큼 잘 쪄진 식빵과 같은 향이 나는 막걸리. 알코올 도수 9%인 만큼 묵직한 뒷맛도 특징이다. 이원 막걸리가 나오는 옥천의 금강 상류에서 잡히는 빙어 요리인 도리뱅뱅이를 추천하고 싶다. 묵직한 막걸리인 만큼 식감이 가벼운 도토리묵, 겉절이, 나물 등과 잘 어울린다.',2,3,3,5,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(518,'허니문와인',10,35000,'국내산 벌꿀','아이비영농조합법인','경기도 양평군 강하면 왕창부로길 110','경기도 양평군','ETC','입안에 머금은 순간 꿀의 맛과 향이 거침없이 올라온다. 기존의 꿀과 다른 점은 진득하지 않다는 것. 맑고 긴 여운을 가진 기분 좋은 단맛이 있다. 사과 향, 배 향도 같이 느껴지는 것도 특징이다. 차게 해서 식전주 및 식후주로 추천한다.',4,2,2,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(519,'허니비 와인',8,35000,'국내산 벌꿀','아이비영농조합법인','경기도 양평군 강하면 왕창부로길 110','경기도 양평군','ETC','물처럼 연한 황금색을 가지고 있으며 달콤한 꿀 향기가 술을 빨리 먹어보도록 만든다. 단맛이 너무 강하지 않게 올라와 입 안을 채워주고 아주 부드럽게 술을 즐길 수 있게 해준다. 꿀의 잔맛이 술을 다 마시고도 끝까지 남아 술이 너무 가볍게 되는 것을 막아 준다. 맛있게 즐길 수 있는 술이다.',3,1,1,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(520,'호담 산양산삼 막걸리',6,16000,'쌀(경기미), 입국, 산양산삼, 정제수, 아스파탐','대농바이오우리산삼','경기도 광주시 퇴촌면 천진암로 684','경기도 광주시','TAK','입국과 쌀을 이용한 하얀색의 생막걸리로 곡물 발효 향과 인삼 향이 가득하며, 단맛이 적고 신선한 산미가 전체적으로 술을 깔끔하게 만들어 주는 것이 특징이다. 입 안에 넣으면 삼의 느낌이 꽉 차고 알코올 도수가 높지 않아 자극적이지 않고 편하게 즐길 수 있다. 김치전과 잘 어울린다.',1,3,3,1,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(521,'호담 산양산삼주',13,4000,'산양산삼, 정제주, 주정','대농바이오우리산삼','경기도 광주시 퇴촌면 천진암로 684','경기도 광주시','CHE','물처럼 맑은 황금색을 가진 술로 은은하게 인삼향이 올라온다. 단맛과 신맛의 조화가 잘 갖춰져 있으며 독특하게 잘 만든 육포의 맛이 난다. 술을 마시고 난 후에 은은하게 남는 쓴맛과 알코올이 뒷맛을 묵직하게 만들어준다. 백설기 등과 같은 떡과 잘 어울린다.',1,1,1,3,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(522,'호담',20,18000,'산양산삼, 정제수, 주정','대농바이오우리산삼','경기도 광주시 퇴촌면 천진암로 684','경기도 광주시','SPI','예쁘고 작은 병 안에 산양산삼이 들어있다. 연한 미색을 가지고 있고 신선한 삼향이 코에 가득히 올라온다. 삼의 쓴맛이 알코올과 희석되어 혀를 자극시킨다. 알코올 도수가 높지 않아 약용주로 편하게 마실 수 있는 술이다. 닭백숙과 잘 어울린다.',2,0,0,2,0,2,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(523,'호땅',6,2700,'쌀(국내산), 국, 효모, 정제수, 땅콩(국내산)','배혜정도가','경기도 화성시 정남면 문학리 674-23','경기도 화성시','ETC','쌀로 빚은 술에 땅콩을 넣어 빚은 술이다. 술 빛깔도 땅콩색에 가깝다. 발효과정에 땅콩을 넣어도 땅콩맛이 잘 나지 않기 때문에 호도, 땅콩, 커피향 등을 적절히 조합해 맛있는 땅콩 음료를 마시는 듯 하다. 술을 잘 마시지 못하는 분들도 맛있게 즐길 수 있는 술이다. 땅콩, 호두 등 견과류와 편하게 마시기 좋은 술이다.',3,1,2,4,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(524,'호랑이 생 막걸리',6,2200,'쌀, 정제수, 국, 효모, 젖산 등','배혜정 도가','경기도 화성시 정남면 문학리 674-23','경기도 화성시','TAK','주원료인 쌀의 특징이 잘 살아있으며, 단맛이 강하고 산미가 적은 호랑이처럼 묵직한 막걸리다. 탄산에 의한 청량감은 강하지 않지만 맛이 진해 너무 가벼운 음식보다는 탕수육과 같이 중국음식과 잘 어울린다.',4,1,2,4,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(525,'호모루덴스',12,12000,'쌀, 누룩, 정제수','농업회사법인(주)산수','강원도 홍천군 화촌면 야시대로 211-57','강원도 홍천군','TAK','연한 아이보리 색을 가지고 있으며 은은하게 달콤한 향이 올라오지만 맛은 단맛과 신맛이 적고 연한 쓴맛으로 술이 매우 드라이하다. 일반 막걸리에 비해 도수가 높아(12%) 입 안의 풍미가 강하고 술을 마시고 난 후에도 긴 여운이 남는다. 돼지 목살 숯불 구이와 잘 어울린다.',1,2,2,4,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(526,'혼디주',12,8000,'감귤(제주산)100%, 정제수, 백설탕, 구연산, 무수아황산','농업회사법인(주)시트러스','제주특별자치도 서귀포시 남원읍 신례천로46','제주특별자치도 서귀포시','WIN','시트러스한 향이 그대로 느껴지는 술. 향기로운 제주도 특유의 감귤 맛이 향과 맛, 그리고 식감 전체에 살아있으며 특히 마시고 난 여운에서 기분 좋은 신맛이 계속 남는다. 제주도 흑돼지 구이와 즐긴다면 기름진 부분을 잘 잡아줄 것으로 보인다.',4,3,3,2,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(527,'홍로',45,50000,'백련, 찹쌀, 멥쌀, 누룩, 자초','영농조합법인 석전상온전통주가','경상북도 칠곡군 왜관읍 동산2길19','경상북도 칠곡군','SPI','자초 특유의 아름다운 붉은색을 가진 증류주다. 향에서는 고운 흙향이 다가오다가 이내 카리스마가 있는 술향으로 마무리된다. 입에 머금는 순간 진한 자초 꽃에서 나올 듯 한 나는 부드러운 향기와 알코올 도수 45%의 강렬한 증류주가 맞붙다가 이내 차분히 가라앉는다. 독특한 술맛으로 다른 안주보다는 술맛에 집중하는 것을 추천해 본다. 처음에 적응되지 않은 맛으로 접근이 어렵다면 얼음과 탄산수, 거기에 레몬을 넣어 마셔도 충분히 이 술의 매력은 즐길 수 있다.',0,0,0,2,0,4,2);
