insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(542,'희양산막걸리 15도',15,9500,'쌀(국내산), 누룩, 정제수','두술도가','경상북도 문경시 가은읍 가은5길 7','경상북도 문경시','TAK','밀누룩을 이용해 진한 아이보리 빛깔을 갖고 있으며 신선한 곡물향이 매력적인 술이다. 전체적으로 단맛보다는 산미가 있지만 곡물의 고소함이 함께해 조화를 잘 이루고 있다. 일반 막걸리에 비해 도수(15%)가 높아 큰 잔 보다는 약주잔 좋다. 저녁에 다양한 반찬과 함께 반주로 딱 좋은 술이다.',3,4,1,5,0,0,0);
insert into drink(did,name,alc,price,ingre,maker,address,region,category,info,sweet,sour,cool,body,balance,insense,throat) values(543,'희양산막걸리 9도',9,8000,'쌀(국내산), 누룩, 정제수','두술도가','경상북도 문경시 가은읍 가은5길 7','경상북도 문경시','TAK','은은한 아이보리 빛깔의 술로 구수한 곡물향이 좋다. 산뜻한 산미가 입 안을 꽉 채워 시원한 느낌을 준다. 목넘김이 부드럽고 혀에 남는 잔 곡물의 느낌이 매우 좋다. 언제 어디서든 편하게 마실 수 있는 술이다. 같은 양조장의 15% 막걸리가 식사와 함께 하기 좋은 술이라면 이 술은 열심히 일 한 후에 고기류와 편하게 마실 수 있는 술이다.',2,3,2,3,0,0,0);
